DROP TABLE IF EXISTS `ife_review`;
CREATE TABLE `ife_review` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `team` varchar(255) DEFAULT NULL,
  `user` varchar(255) DEFAULT NULL,
  `score` varchar(5) DEFAULT NULL,
  `comment` text,
  `parent` varchar(5) DEFAULT NULL,
  `work` varchar(5) DEFAULT NULL,
  PRIMARY KEY (`id`),
  FULLTEXT KEY `text` (`comment`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

