-- ----------------------------
--  Table structure for `ife_review`
-- ----------------------------
DROP TABLE IF EXISTS `ife_review`;
CREATE TABLE `ife_review` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `team` varchar(255) DEFAULT NULL,
  `user` varchar(255) DEFAULT NULL,
  `score` varchar(5) DEFAULT NULL,
  `comment` text,
  `parent` int(5) DEFAULT NULL,
  `work` int(5) DEFAULT NULL,
  `time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `work` (`work`),
  KEY `work_2` (`work`),
  KEY `work_3` (`work`),
  FULLTEXT KEY `text` (`comment`)
) ENGINE=InnoDB AUTO_INCREMENT=1745 DEFAULT CHARSET=utf8mb4;

-- ----------------------------
--  Records of `ife_review`
-- ----------------------------
BEGIN;
INSERT INTO `ife_review` VALUES ('27', 'code matters', '彭君怡', '9', '你好，我也是打算用React写，看了你的DEMO感觉很棒，请问你们用的是什么方式去动态添加那些单选题，多选题的选项框？现在卡在这里了。。你们核心代码压缩了一直摸不清头绪。。。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1092><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">05-13 18:06</span></p><p class=\"reply-content\">从阶段一开始就是一个人在写。那段是在Edit里。最好先看懂Redux吧。</p></li><li class=\"reply\" data-id=1091><p class=\"reply-info\"><a href=\"/user/profile?userId=5320\">彭君怡</a>&nbsp;回复&nbsp;<span class=\"time\">05-13 17:57</span></p><p class=\"reply-content\">一个人写！NB！  我在想是不是要把新增的添加到一个数组里，然后遍历数组渲染。。  那个应该是放在Edit的jsx里吧？ 说实话看了半天没搞清思路 哎。。 看着看着又逛到你推荐的几个知乎和专栏里看了看。。 更懵了 绝望(っ °Д °;)っ</p></li><li class=\"reply\" data-id=1090><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">05-13 17:17', '0', '11105', '2016-05-13 15:29:00'), ('28', null, '颜适', null, '从阶段一开始就是一个人在写。那段是在Edit里。最好先看懂Redux吧。', '27', null, '2016-05-13 18:06:00'), ('29', null, '彭君怡', null, '一个人写！NB！  我在想是不是要把新增的添加到一个数组里，然后遍历数组渲染。。  那个应该是放在Edit的jsx里吧？ 说实话看了半天没搞清思路 哎。。 看着看着又逛到你推荐的几个知乎和专栏里看了看。。 更懵了 绝望(っ °Д °;)っ', '27', null, '2016-05-13 17:57:00'), ('30', null, '颜适', null, '去看src下的代码，没压缩的。就是加了个点击事件，修改了state再重新渲染输入框而已。以及我是一个人写的啊～', '27', null, '2016-05-13 17:17:00'), ('31', 'steel team', '欧阳湘粤', '8', '虽然部分地方有BUG 但是已经做的挺不错的了<br />\r\n基本功能都实现了</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1083><p class=\"reply-info\"><a href=\"/user/profile?userId=1110\">欧阳湘粤</a>&nbsp;回复&nbsp;<span class=\"time\">05-10 21:37', '0', '11105', '2016-05-10 21:34:00'), ('32', null, '欧阳湘粤', null, '我花14天，除了使用Sass 和 requireJS<br />\r\n从组件到框架 全部一个人手写...原生Js 1800行....<br />\r\n最后实在写不下去了...<br />\r\n截止前，考虑还是把烂尾的工程丢上去，让其他同学吸取教训T_T', '31', null, '2016-05-10 21:37:00'), ('33', '组团来卖萌的', '周天树', '8', '还在学习react，所以技术细节就没资格评论了TAT单纯从Demo体验评论吧。。。做的还是相当不错啊，而且因为是React，组件化程度肯定比我用Angular高到不知道哪里去了，以后项目维护起来也要方便的多。而且有些地方的交互体验很好，比如浮出层的弹出效果。。。<br />\r\n不过有几个小BUG:<br />\r\n 1.主页面上的“时间”那里貌似有点问题，编辑完问卷之后时间并没有更新<br />\r\n 2.编辑页面中，保存问卷和发布问卷的按钮点击事件还可待商榷，比如点击“保存问卷”以后，我个人是觉得应该直接返回主页比较恰当，然后点击“发布问卷”时的一些操作也还有点小问题，比如截止时间早于当前日期时也能成功发布，然后发布了之后首页的状态显示的是“已结束”。。。<br />\r\n<br />\r\n当然，我觉得这些都是小问题，debug起来肯定是快的，个人觉得做这个项目的过程中对框架的理解和运用的经验才是最重要的（逃<br />\r\n<br />\r\nBUG测试用例：新建问卷-&gt;不填截止时间直接发布问卷-&gt;点击“确认”</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1082><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">05-10 19:41</span></p><p class=\"reply-content\">哈哈哈哈随便添加一个问题后发布的话有Bug！<br />\r\n在测试问卷合法性的函数里面，检查时间的getFullYear漏了加括号，而且这还不会报错！因为那样子返回的是一个函数，函数是true啊摔！<br />\r\n笑哭了，现在在上课传不了Github，回头改【顶锅盖逃</p></li><li class=\"reply\" data-id=1081><p class=\"reply-info\"><a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">05-10 19:29</span></p><p class=\"reply-content\">“时间”那里。。。好吧是我理解错误，你的“时间”那栏是截止时间，而我理解的是最后修改问卷的时间TAT。。。<br />\r\n测试样例我刚才又试了一下，还是一样，我是 新建问卷-&gt;随便添加一个问题-&gt;直接按“发布问卷”。。。如果你这样试了还是没问题那就无视我吧（逃</p></li><li class=\"reply\" data-id=1080><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">05-10 18:49</span></p><p class=\"reply-content\">补充一下，我测了一下你的样例，没有问题诶~<br />\r\n不填时间的话会弹出“请设置问卷截止日期”的呀TAT</p></li><li class=\"reply\" data-id=1079><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">05-10 18:44', '0', '11105', '2016-05-10 18:33:00'), ('34', null, '颜适', null, '哈哈哈哈随便添加一个问题后发布的话有Bug！<br />\r\n在测试问卷合法性的函数里面，检查时间的getFullYear漏了加括号，而且这还不会报错！因为那样子返回的是一个函数，函数是true啊摔！<br />\r\n笑哭了，现在在上课传不了Github，回头改【顶锅盖逃', '33', null, '2016-05-10 19:41:00'), ('35', null, '周天树', null, '“时间”那里。。。好吧是我理解错误，你的“时间”那栏是截止时间，而我理解的是最后修改问卷的时间TAT。。。<br />\r\n测试样例我刚才又试了一下，还是一样，我是 新建问卷-&gt;随便添加一个问题-&gt;直接按“发布问卷”。。。如果你这样试了还是没问题那就无视我吧（逃', '33', null, '2016-05-10 19:29:00'), ('36', null, '颜适', null, '补充一下，我测了一下你的样例，没有问题诶~<br />\r\n不填时间的话会弹出“请设置问卷截止日期”的呀TAT', '33', null, '2016-05-10 18:49:00'), ('37', null, '颜适', null, '感谢Review。<br />\r\n时间那里应该是没有问题的，编辑完问卷如果不保存直接返回的话是会放弃编辑效果的。所以我打算在我的问卷那个按钮那里再加个弹出框，提示是否放弃当前编辑。<br />\r\n保存问卷那里我也觉得有些别扭，不知怎样改才好。我原本想直接返回的，但是你玩过Diablo2么……后来改成不返回了，交互却有些麻烦，又不能不提示用户已经保存了。当然，这一切都是设计师的锅【逃', '33', null, '2016-05-10 18:44:00'), ('38', 'AlphaRun', '颜适', '6', '看到10分就进来读了源码。<br />\r\n<br />\r\n交互体验：<br />\r\n1. 没有问卷的时候应该显示大大的新建问卷，而点击小的新建问卷应该直接进入编辑问卷页，而不是再点击一次大的新建问卷。<br />\r\n2. 添加问题那里实现得不合理。不应该在添加问题的时候才能编辑问题和选项的，这样也使得复用问题的功能变得非常的蠢……<br />\r\n3. 问题较多时，弹出日历的交互不好。日历显示后还要把滚动条往下拖才能看到日历。<br />\r\n4. 未发布的问卷不应该显示查看数据的，同理已发布的问卷不显示编辑，不能删除。<br />\r\n<br />\r\n当然，上面的都是小问题。<br />\r\n一些Bug：<br />\r\n1. 发布问卷后，发布成功的弹出框有重大Bug。<br />\r\n2. 问卷没有问题时，直接发布问卷，然后添加问题，有Bug，而且是一系列的Bug。<br />\r\n3. 添加问题后，直接编辑标题，然后选择问题类型，有Bug。<br />\r\n<br />\r\n源码：<br />\r\n1. 目录有点混乱，一般用gulp或者webpack的应该有src和build之类的文件夹吧，而不是全部放在app下面。<br />\r\n2. px布局是一种非常差的实践，维护困难。换成rem或者em吧。<br />\r\n3. sass白用了，没看到任何的mixin，变量也只有三种颜色。不如直接写css吧。<br />\r\n4. css可以按照模块写，而不是全部写在一个文件里，便于维护。<br />\r\n5. js能讲的就太多了，我就看了下util。最大的问题是没有拥抱es6，还在用很老的写法。实现上能挑的细节就更多了，cloneObject那里不应该用instance，创建新数组的话，es5的最佳实践应该是slice而不是写一大段for，es6用...可以秒杀。数组去重也不应该用二重循环，起码用对象写个hash吧，而且这个在es6里就是new Set一行的事情……写得太繁琐了。其他就不一一看了。<br />\r\n<br />\r\n打个及格分鼓励一下原生。<br />\r\n这是我的提交，欢迎互评。http://ife.baidu.com/review/detail?workId=11105</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1105><p class=\"reply-info\"><a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">05-21 14:27</span></p><p class=\"reply-content\">但你才大二，前途无量。</p></li><li class=\"reply\" data-id=1104><p class=\"reply-info\">作者<a href=\"/user/profile?userId=1133\">巫海维</a>&nbsp;回复&nbsp;<span class=\"time\">05-21 11:14', '0', '11110', '2016-05-20 13:23:00'), ('39', null, '颜适', null, '但你才大二，前途无量。', '38', null, '2016-05-21 14:27:00'), ('40', null, '巫海维', null, '真心非常感谢您的review！！！！！<br />\r\n其实我也知道前面给了10分的那位朋友给的分太高了TT...就知道会引来大神吊打...<br />\r\n关于util的实现我都是直接照搬ife2015春季学期的实现...也没有怎么看其他的...<br />\r\nes6只看了一点点...各种框架也还没开始看....而且到后期很多时候我都感觉是在硬写...不够优雅,,,很久没有看过其他人更好的实现（很不好的学习习惯啊！喂！）...感觉好久没有什么进步了...<br />\r\n有时间会去学react然后去认真看您的实现的！<br />\r\n<br />\r\n关于各种批评，确实sass白用了...目录是自己觉得怎么方便怎么来的（很不好啊！）...各种js也是...感谢指出！<br />\r\n<br />\r\n感觉您和我的学习轨迹有点相近，但是现在看差距却已经不小了啊...同先做了ife2015再来做这一期...同EE...（看了一下您的blog和知乎，三个月做成这样真的好厉害！我做了四五个月水平还是不忍直视...）<br />\r\n<br />\r\n最近各种电路信号系统...各种考试...估计要认真改要等到暑假了...总而言之，非常感谢review！！祝共同进步！！希望您能很快拿到心仪的offer~！感谢！！', '38', null, '2016-05-21 11:14:00'), ('41', 'steel team', '欧阳湘粤', '10', '同大二，同技术栈（native 封的utilJs requireJs sass Localstorage）同队友打酱油...<br />\r\n逻辑处理到两个页面交互时遇到问题，逻辑乱一锅粥<br />\r\n<br />\r\n表示会努力分析你的code，面壁</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1098><p class=\"reply-info\">作者<a href=\"/user/profile?userId=1133\">巫海维</a>&nbsp;回复&nbsp;<span class=\"time\">05-17 21:38', '0', '11110', '2016-05-17 10:14:00'), ('42', null, '巫海维', null, '谢谢review！！！！！（个人感觉10分给的有点高啊&gt;&lt;。。。还是有不少问题的）关于两个页面的交互的问题可以看看 http://www.zhangxinxu.com/wordpress/2012/02/html5-web-messaging-cross-document-messaging-channel-messaging/ 这个链接里面的内容，感觉讲得很详细，而且例子也不错~但是postMessage在手机端没有反应（跳出的页面一片空白....）....心塞...所以手机端强行用了localStorage....感觉不是很好....<br />\r\n<br />\r\n感谢review！共同进步！~', '41', null, '2016-05-17 21:38:00'), ('43', 'We Are The World', '徐智鹏', '5', '提几个细节的问题<br />\r\n1.已发布的问卷，用户不能查看问卷（作答不了）<br />\r\n2.首页复选框取消不了<br />\r\n3.echarts最好采用异步加载到页面上</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1070><p class=\"reply-info\">作者<a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">05-05 19:59', '0', '10930', '2016-05-05 15:07:00'), ('44', null, '周天树', null, '感谢你的review~~<br />\r\n1的问题，我当时是参照设计图设计的按钮，既然这样不好的话，我考虑把样式改一改了。<br />\r\n2和3都是很好的建议，非常感谢！', '43', null, '2016-05-05 19:59:00'), ('45', 'TEAM-SZ', '郭旭', '8', '一个bug：在新建问卷的页面，如果没有给出截止日期或者添加问题，就会返回前一个页面，而不是留在当前页面等待用户输入</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1069><p class=\"reply-info\">作者<a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">05-04 23:30', '0', '10930', '2016-05-04 20:42:00'), ('46', null, '周天树', null, '对的TAT，这就是我说的(2)， angular-router用的还不是很熟，依然要继续学习一个。。。<br />\r\n感谢你的review~~', '45', null, '2016-05-04 23:30:00'), ('47', 'BATCTO', '郭江伟', '5', '题目复用之后，添加选项会导致两道题都变化。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1066><p class=\"reply-info\">作者<a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">05-04 10:43', '0', '10930', '2016-05-04 08:17:00'), ('48', null, '周天树', null, '感谢您的review，已debug，原bug是由于复用问题时我草率的使用了浅复制来拷贝原对象造成的。。。', '47', null, '2016-05-04 10:43:00'), ('49', null, null, '6', '### 有部分功能未实现或存在问题：<br />\r\n<br />\r\n1. 最上面的问题没有上移操作，最下面的问题没有下移操作<br />\r\n2. 编辑问卷后刷新页面，edit.js 85 行报错<br />\r\n3. 编辑完后变成了新建问卷页面<br />\r\n4. 对表单输入的合法性缺少必要的验证<br />\r\n5. 顶部导航“我的问卷”缺少链接<br />\r\n<br />\r\n### HTML<br />\r\n<br />\r\n1. 嵌入外部样式的 link 标签同时使用 rel 和 type 属性<br />\r\n2. 代码缩进过于随意<br />\r\n3. 表单界面应该使用 form 标签<br />\r\n4. 根据模板逻辑，在循环内的标签中出现了 id，将导致页面存在多个相同 id<br />\r\n5. edit.js 中包含对 calendar.js 的依赖，但是两个 js 的引入方式将导致执行顺序的不确定，从而导致出现 bug，可见对 AMD 不够熟悉，对 require.js api 也不熟悉<br />\r\n6. 页面内容分明是中文，但 html 标签的 lang 属性值却为 en<br />\r\n7. script 标签不作为模板容器时，type 属性冗余<br />\r\n<br />\r\n### CSS<br />\r\n<br />\r\n1. 部分 class 命名过于随意，未能体现语义<br />\r\n2. 代码规范不统一，比如有的 { 前有空格，有的没有，同样有的冒号 : 后有空格有的没有<br />\r\n3. 颜色取值风格不统一，有大写有小写<br />\r\n4. 有的地方 0 值带单位，有的地方不带<br />\r\n5. 建议不同浏览器的专有属性使用 postcss 来处理，降低开发及维护成本<br />\r\n6.  SASS 的优势没有体现出来，还是当成可嵌套书写的 CSS 来使用<br />\r\n7. 用了 normalize 但仍出现 * {} 之类的代码<br />\r\n<br />\r\n### JavaScript<br />\r\n<br />\r\n1. Calendar 组件中 id 的默认值，对所有实例应该是生成不同的值（比如增加自增数字）<br />\r\n2. Calendar.prototype 的 constructor 没有修复<br />\r\n3. 建议再研究下获取某月天数的算法<br />\r\n4. editCommon 的 api 首字母大小写不统一<br />\r\n5. 很多用于调试的 console.log 仍然留在代码中，建议使用开发者工具调试<br />\r\n6. 有的页面用 jQuery 和 require.js，有的页面用原生，看起来是不同的人在开发，说明团队没有沟通好达成一致？<br />\r\n7.  使用了 require.js 的页面，仍然存在大量的全局变量<br />\r\n8. 代码风格不统一，缩进随意<br />\r\n<br />\r\n整体功能实现上已经比较完整，还能自己编写部分组件，难能可贵，但是对于团队合作方面未能体现。项目的改进方向，建议将持久化抽象独立出一个层，第三方依赖统一使用 npm 管理，构建使用 webpack，构建前按约定作代码风格的检查。', '0', '11132', '2016-05-27 11:33:00'), ('50', 'AlphaRun', '颜适', '5', '细节上前面那位已经挑了不少问题了，我从大的方面讲一下。<br />\r\nvue有一套官方推荐的技术栈，vue + vuex + vue-router + webpack。<br />\r\n你可以从这方面入手。', '0', '11107', '2016-05-11 12:34:00'), ('51', '前端泡沫组', '巫海维', '6', '发现了一些bug或者不足，和你们说一声吧~<br />\r\n1，新建页面（或者编辑页面）点击新建问题时候，可以不停地按，然后不停地出....应该加一个检测，不能反复再按<br />\r\n2，单页应用的前进后退可以用hash来模拟一下，因为用户其实很多时候都很习惯按浏览器返回页.....（当然我也想他们按logo啊！）<br />\r\n3，按下删除的时候，无法退出....除非刷新页面，给后面的黑色背景加一个事件吧~<br />\r\n4，标题那里我可以用空格当标题...感觉还是设一个限制比较好吧~<br />\r\n5，浏览器大问题....<br />\r\n<br />\r\n总体来说样式还是挺好看的，各部分功能也基本完成了。总体来说说代码还是比较清晰的（没用过vue...不过看着很不错的样子...）<br />\r\n<br />\r\n希望共同进步~！加油！', '0', '11107', '2016-05-11 10:10:00'), ('52', 'steel team', '欧阳湘粤', '9', '真大神', '0', '11114', '2016-05-21 14:18:00'), ('53', 'frontendless', '贺乐', '9', '看了代码觉得汗颜，大神，给赞', '0', '11114', '2016-05-20 22:14:00'), ('54', 'LZZ神秘代码', '周延博', '10', '参考大神的代码，自己写了一个，上传了，欢迎review，不过有很多bug，还有一两个功能没实现，会继续改进，不会因为任务结束就不管了，希望能交个朋友，微信号zyb118114，QQ号282636120，github：perseveringman，希望之后能一起讨论前端相关的东西，共同进步！', '0', '11108', '2016-05-22 22:12:00'), ('55', '前端小白学习', '刘洋', '7', '木桶布局里，当加入的图片正好是一行的最后一张时，这张图片只能显示一半，我在第三阶段学习过你的代码，也有这个问题。我也尝试改进这个问题，但是没有想出办法。<br />\r\n<br />\r\n另外，当gutter不为零时，有一些行的宽度要小一点。', '0', '11108', '2016-05-17 23:34:00'), ('56', 'Rivers', '赵晓川', '9', '作者你好，关注你们的代码很久了，觉得可读性真的很好。这个有些复杂，能不能简单的讲一下你们的思路。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1096><p class=\"reply-info\"><a href=\"/user/profile?userId=5636\">赵晓川</a>&nbsp;回复&nbsp;<span class=\"time\">05-16 13:44</span></p><p class=\"reply-content\">好的，谢谢。看过你的回复之后，我已经对代码的结构有了一个大致的了解。打算接下来再好好看下你的代码。<br />\r\nps:上一阶段的任务基本上也是在看过你的代码之后基本上完成功能。 非常谢谢，感觉这次百度任务最精髓的地方就是review。 哈哈</p></li><li class=\"reply\" data-id=1095><p class=\"reply-info\">作者<a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">05-15 23:00', '0', '11108', '2016-05-15 22:20:00'), ('57', null, '赵晓川', null, '好的，谢谢。看过你的回复之后，我已经对代码的结构有了一个大致的了解。打算接下来再好好看下你的代码。<br />\r\nps:上一阶段的任务基本上也是在看过你的代码之后基本上完成功能。 非常谢谢，感觉这次百度任务最精髓的地方就是review。 哈哈', '56', null, '2016-05-16 13:44:00'), ('58', null, '余思烨', null, '假设你已经了解了第三阶段的相册内容，那第四阶段的任务可以理解为给第三阶段的库做一个整合并添加上一些其他功能。<br />\r\n<br />\r\n这里我写的库可以分为两部分来看，一部分是负责布局部分，一部分是全屏部分。布局部分由几个东西串联，包括图像 URL，获取 DOM 元素，重新布局（清空布局，添加对应 DOM 元素）。这里的重点在于，把添加图像的部分提出来复用，给对应的四个布局。全屏布局的部分包括，全屏状态，鼠标事件，图像 URL。另外我把相对应的设置提取出来保存在私有变量 _options 中，通过这个变量保存各个部分的状态从而把各个部分串联起来。<br />\r\n<br />\r\n这样讲你能理清楚思路吗？', '56', null, '2016-05-15 23:00:00'), ('59', 'xtu', '樊玉莹', '9', 'http://placehold.it/1300x1250/449F93/fff  这个文件好像没有上传上去，小白就发现了这个问题</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1094><p class=\"reply-info\">作者<a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">05-15 18:56', '0', '11108', '2016-05-15 08:47:00'), ('60', null, '余思烨', null, '谢谢你的 review，除了第一张开始的封面，其他图片我都没有传上去的，你可以参考一下第三阶段相册任务中的参考资料，里面有提到 PLACEHOLD.IT 用来占位的不同大小的图片生成工具。也就是说这些图片都是直接由这个网站的服务器生成的，所以如果你看不到图片的话，由于是国外的网站，所以可能造成图片加载不出来的问题，请重新刷新一下，或者清一下缓存就可以解决了。', '59', null, '2016-05-15 18:56:00'), ('61', 'icoding', '冯文峰', '5', '第一关，最后和boss同时死掉，显示game over，然后显示you win，跳到第二关  就看不到自己了，单独打开是正常的', '0', '11121', '2016-06-04 19:04:00'), ('62', '前端热爱者', '吴立宁', '10', '很厉害', '0', '11121', '2016-05-24 00:33:00'), ('63', '前端社区网团队', '莫宇杰', '10', '大神收下我膝盖。。。', '0', '11121', '2016-05-23 14:31:00'), ('64', 'steel team', '欧阳湘粤', '7', '怎么说呢，我玩了半天发现都是死路 怎么都走不到终点~<br />\r\n后来才发现用空格可以清墙壁。。。。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1093><p class=\"reply-info\"><a href=\"/user/profile?userId=5337\">张志豪</a>&nbsp;回复&nbsp;<span class=\"time\">05-14 15:09</span></p><p class=\"reply-content\">这个空格真是666</p></li><li class=\"reply\" data-id=1077><p class=\"reply-info\">作者<a href=\"/user/profile?userId=207\">唐伟豪</a>&nbsp;回复&nbsp;<span class=\"time\">05-09 12:47', '0', '11104', '2016-05-09 11:01:00'), ('65', null, '张志豪', null, '这个空格真是666', '64', null, '2016-05-14 15:09:00'), ('66', null, '唐伟豪', null, '哈哈这个是隐藏作弊技能..', '64', null, '2016-05-09 12:47:00'), ('67', 'steel team', '欧阳湘粤', '6', '同感，我也写懵逼了<br />\r\n<br />\r\n6分是你们的页面起码能跑，希望你们早日完成这个program，我再回来改</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1062><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3962\">罗建军</a>&nbsp;回复&nbsp;<span class=\"time\">05-03 13:22', '0', '10952', '2016-05-03 13:18:00'), ('68', null, '罗建军', null, '谢谢 我会尽快做完更新的', '67', null, '2016-05-03 13:22:00'), ('69', '斯巴达', '周敏', '10', '请问代码目录结构怎么写的呀~', '0', '8833', '2016-05-02 23:14:00'), ('70', 'imooc-慕星人', '阮小泽', '10', '满分。', '0', '8833', '2016-04-30 00:23:00'), ('71', '前端刷刷队', '张圣兰', '9', '恩，知道了一些功能的实现原理', '0', '8833', '2016-04-23 18:18:00'), ('72', '创享前端', '苏富波', '9', '你js代码开始的那个注释怎么做的，就是那个目录结构怎么写的', '0', '8833', '2016-04-22 18:51:00'), ('73', '对对队', '张奇琦', '10', '冲这才一百多行代码也要给满分啊，要求全实现了，等我好好研究再更新review<br />\r\n先膜拜一下大神', '0', '8833', '2016-04-21 21:54:00'), ('74', 'AllyPowers', '娄聪', '9', '嗯，这才有点组件的味道吗！', '0', '10809', '2016-05-12 21:34:00'), ('75', '学习路上的小学生', '龚克分', '10', '非常不错， 学到了很多， 封装，居中，解耦都很好', '0', '10809', '2016-05-05 08:46:00'), ('76', '无用改革', '刘夏', '9', '封装得非常好，学到了很多。但是mousedown绑定了好多次，感觉可以优化，用闭包……', '0', '10809', '2016-04-28 11:06:00'), ('77', '犬次郎联盟', '罗春芳', '9', '代码封装得非常好，通用性强，有一个不明白的地方是<br />\r\n浮动框的这个布局：left: 50%; top: 50%;transform: translate(-50%, -50%)，为什么使用了translate(-50%, -50%)以后会显示居中呢？是怎么计算的？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1010><p class=\"reply-info\">作者<a href=\"/user/profile?userId=10454\">孟浩</a>&nbsp;回复&nbsp;<span class=\"time\">04-25 13:48', '0', '10809', '2016-04-25 11:34:00'), ('78', null, '孟浩', null, 'left:50% top:50%后是元素的左上角在中间，translate(-50%,-50%)是横纵坐标-自身50%的宽高，这样就居中了', '77', null, '2016-04-25 13:48:00'), ('79', '对对队', '张奇琦', '9', '1.一部分区域可以拖拽这点挺好，但是这区域有点小<br />\r\n2.mask这个元素可以在html里写，因为无论页面多少弹窗都可以用一个mask</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1009><p class=\"reply-info\">作者<a href=\"/user/profile?userId=10454\">孟浩</a>&nbsp;回复&nbsp;<span class=\"time\">04-25 13:45', '0', '10809', '2016-04-24 22:55:00'), ('80', null, '孟浩', null, '原本是准备把可拖动区域当参数传的。<br />\r\n都用同一个mask是个好建议，也当参数传过来，不传就用现在的当默认的。', '79', null, '2016-04-25 13:45:00'), ('81', 'ZLFE', '刘彬德', '9', '赞', '0', '8935', '2016-04-21 14:11:00'), ('82', 'neutomato', '蒋俊', '9', '很不错', '0', '8935', '2016-04-18 11:26:00'), ('83', 'AlphaRun', '颜适', '8', '拖拽做得很好啊，那个正则是亮点，学习了。把构造函数写在原型里真是第一次见，感觉Popuper和init的关系有点混乱啊，这段没看明白呢。', '0', '8935', '2016-04-14 00:24:00'), ('84', '学好了去实习', '张帅', '8', '我见过最简洁的代码了，不过拖拽功能可以考虑边框范围内拖动', '0', '8871', '2016-04-29 17:07:00'), ('85', '调皮鬼与玩偶', '唐叶', '8', '功能还可以再完善，例如实现拉宽、拉长。不过代码真的很简洁', '0', '8871', '2016-04-24 20:33:00'), ('86', '前端刷刷队', '张圣兰', '9', '这个拖拽好简洁！', '0', '8871', '2016-04-23 20:53:00'), ('87', 'Ripple Team', '秦圆圆', '9', 'hahhacopen好看  代码清晰', '0', '8871', '2016-04-22 14:23:00'), ('88', 'UoM队', '江一明', '8', '哈喽~~同为模式啥的要继续好好学人士~~ 然后想提个小建议：拖拽框能拖拽的限度最好设置一下呢，我直接拖出整个页面了不太友好啦~^^', '0', '10792', '2016-04-26 00:09:00'), ('89', '海贼兄弟', '孟浩', '8', '好简洁。<br />\r\n不过耦合度比较高，不能复用。做个大型的项目如果每个界面都有好几种不同的弹出层这么写就累死了。<br />\r\n缩放我也没做，原本准备用scale做的。。。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1004><p class=\"reply-info\">作者<a href=\"/user/profile?userId=4561\">张奇琦</a>&nbsp;回复&nbsp;<span class=\"time\">04-24 22:50', '0', '10792', '2016-04-23 22:53:00'), ('90', null, '张奇琦', null, '的确，模式这块我得好好学学', '89', null, '2016-04-24 22:50:00'), ('91', null, null, '6', 'LIne 16: 非常多的重复代码，可以精简一下<br />\r\nLine 44: 所有的事件处理函数都不应该是一个组件外部的变量，应该是对象里的一个方法，如 onDrage (e) {// ...}<br />\r\nLine 79: 参数校验这部分不应该放在这里，放到 constrctor 里面<br />\r\nLine 133: unique也不应该在外部定义，应该支持一下页面有多个弹窗<br />\r\nLine 155: 重复代码<br />\r\nvar THEMES = {<br />\r\n    success: &#039;Success!&#039;<br />\r\n};<br />\r\nvar _this = this;<br />\r\nObject.keys(THEMES).map(function (key) {<br />\r\n    _this.model.className = &#039;POP-model POP-&#039; + key;<br />\r\n    _this.title.innerHTML = THEMES[key];<br />\r\n});<br />\r\nLine 190: 重复代码<br />\r\n[&#039;alert&#039;, &#039;confirm&#039;, &#039;prompt&#039;].map(function (name){<br />\r\n      POP[name] = function(txt, config) {<br />\r\n			var layer = this.single(txt, config);<br />\r\n			if(layer){<br />\r\n				layer[name]();<br />\r\n			}<br />\r\n		};<br />\r\n});', '0', '8836', '2016-04-25 16:55:00'), ('92', 'AlphaRun', '颜适', '9', '操作DOM那段一塌糊涂啊。<br />\r\n一大坨createElement, className, appendChild非常糟糕，可读性为零，效率也低，为什么不考虑innerHTML呢？<br />\r\n还好基本功能实现了。<br />\r\n欢迎互相review。http://ife.baidu.com/review/detail?workId=9157</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=927><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 18:37</span></p><p class=\"reply-content\">= =没讨论单例模式啊，只不过刚好做弹窗用到了，里面的一小部分而已。我觉得我这样维护起来比用innerHTML好多了，整个生成的弹窗的所有部分都是该实例的一个属性，维护起来简单明了..感觉你被你的什么MVC思想扭曲了, 你说前端框架MVC也好MVVM也好也就罢了,,写个小插件也谈MVC.也构不成MVC, 插件只需要做好内部操作, 提供设置接口给外部调用..(关于innerHTML可以去看看面向对象编程, 这是使用innerHTML无法做到的,创建保存DOM节点)</p></li><li class=\"reply\" data-id=926><p class=\"reply-info\"><a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 18:15</span></p><p class=\"reply-content\">我没说单例模式不好。只是不知道为何你如此执着使用大量DOM操作来构建View层，这在维护组件的时候是很蛋疼的。如果你把View层分离，那么以后就算修改组件的交互，只要给View层传入参数，就能得到修改后的结果。但现在的话，你还要先读读那一大坨的DOM操作代码。</p></li><li class=\"reply\" data-id=925><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 17:40</span></p><p class=\"reply-content\">虽然不知道你为什么一直在提MVC, MVC这种东西谁都知道吧,但是我愣是不知道跟代码该怎么写有什么联系,,封装一个插件最关键的还是面向对象编程吧, 每次调用都实例一个唯一的Layer弹窗对象, 所有可操作的节点保存在该对象中, 所有的操作都能在对象内部完成, 如果用innerHTML拼接我是不是每次操作都要去取节点, 而且事件绑定是不是还需要委托,....还有我这种也不是反复插入, 真正插入页面的appendChild只有一处而已, 插入body的</p></li><li class=\"reply\" data-id=923><p class=\"reply-info\"><a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 17:25</span></p><p class=\"reply-content\">我指的效率就是那一堆create和append比innerHTML慢。<br />\r\n以及，如果你知道MVC的话，代码肯定不会写成这样了，这是我说的可读性问题。<br />\r\ninnerHTML的效率问题是不值一提的。你可以先拼接好字符串，再一次插入innerHTML，而不是反复插入。</p></li><li class=\"reply\" data-id=914><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 12:58', '0', '8836', '2016-04-16 11:50:00'), ('93', null, '陈慕杰', null, '= =没讨论单例模式啊，只不过刚好做弹窗用到了，里面的一小部分而已。我觉得我这样维护起来比用innerHTML好多了，整个生成的弹窗的所有部分都是该实例的一个属性，维护起来简单明了..感觉你被你的什么MVC思想扭曲了, 你说前端框架MVC也好MVVM也好也就罢了,,写个小插件也谈MVC.也构不成MVC, 插件只需要做好内部操作, 提供设置接口给外部调用..(关于innerHTML可以去看看面向对象编程, 这是使用innerHTML无法做到的,创建保存DOM节点)', '92', null, '2016-04-16 18:37:00'), ('94', null, '颜适', null, '我没说单例模式不好。只是不知道为何你如此执着使用大量DOM操作来构建View层，这在维护组件的时候是很蛋疼的。如果你把View层分离，那么以后就算修改组件的交互，只要给View层传入参数，就能得到修改后的结果。但现在的话，你还要先读读那一大坨的DOM操作代码。', '92', null, '2016-04-16 18:15:00'), ('95', null, '陈慕杰', null, '虽然不知道你为什么一直在提MVC, MVC这种东西谁都知道吧,但是我愣是不知道跟代码该怎么写有什么联系,,封装一个插件最关键的还是面向对象编程吧, 每次调用都实例一个唯一的Layer弹窗对象, 所有可操作的节点保存在该对象中, 所有的操作都能在对象内部完成, 如果用innerHTML拼接我是不是每次操作都要去取节点, 而且事件绑定是不是还需要委托,....还有我这种也不是反复插入, 真正插入页面的appendChild只有一处而已, 插入body的', '92', null, '2016-04-16 17:40:00'), ('96', null, '颜适', null, '我指的效率就是那一堆create和append比innerHTML慢。<br />\r\n以及，如果你知道MVC的话，代码肯定不会写成这样了，这是我说的可读性问题。<br />\r\ninnerHTML的效率问题是不值一提的。你可以先拼接好字符串，再一次插入innerHTML，而不是反复插入。', '92', null, '2016-04-16 17:25:00'), ('97', null, '陈慕杰', null, '不知道你所说的可读性为零和效率很低是按什么标准来的.<br />\r\n使用createElement创建节点，可以很方便的对每一个创建的节点分别进行操作，绑定移除事件等，可读性不低，我想如果使用的innerHTML的话，使用一串长长的字符串来添加元素节点，然后绑定事件的时候再给document代理点击事件，然后取到innerHTML时附带的class绑定，然后要操作的时候每次都要取一次class。而我的Class只是单纯样式用的。并且innerHTML据我所了解是会替换原节点下面的内容，意味着HTML加载并解析，使得原DOM节点下进行页面重排，而appendChild只会部分重排，所以不知道你的效率低是怎么得出来的，希望能提供相关文档涨涨姿势，虽然我那初始化时一大坨createElement肯定比innerHTML要多,但是我的每一个dom节点是绑定在创建的唯一实例上的，增加或修改各个部分功能肯定比那反锁的取Class操作要方便得多，相信到最后那那所说那种可读性才是真正的零。<br />\r\n最后还是谢谢review，有不对的地方欢迎讨论', '92', null, '2016-04-16 12:58:00'), ('98', '来自远方的他叔叔军团', '孙浩', '8', '看了下代码 很清晰 主要给自己提供了思路</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=980><p class=\"reply-info\"><a href=\"/user/profile?userId=4667\">孙浩</a>&nbsp;回复&nbsp;<span class=\"time\">04-22 10:41', '0', '8836', '2016-04-22 10:35:00'), ('99', null, '孙浩', null, '赞', '98', null, '2016-04-22 10:41:00'), ('100', 'DUT-FEDXXX', '范廷雷', '9', '这个棒，封装性很好！！！！完全可以成为一个独立的组件，而且还有事件的移除，赞！！！</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=915><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 13:13', '0', '8836', '2016-04-16 11:08:00'), ('101', null, '陈慕杰', null, '谢谢支持OvO~~~', '100', null, '2016-04-16 13:13:00'), ('102', '江安职业高中', '梁誉译', '9', '我看了那么多代码我最喜欢的，对于pop进行了封装，用户体验也可以。看了你的代码学习到了很多，谢谢。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=899><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 20:40', '0', '8836', '2016-04-14 20:27:00'), ('103', null, '陈慕杰', null, '谢谢支持~~~', '102', null, '2016-04-14 20:40:00'), ('104', 'code matters', '彭君怡', '5', '(index):69 Uncaught ReferenceError: POP is not defined 楼主这个修改了下？为什么我火狐和谷歌浏览器都报这个错？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=896><p class=\"reply-info\"><a href=\"/user/profile?userId=8861\">梁誉译</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 20:28</span></p><p class=\"reply-content\">并不是火狐的问题或者代码的问题，我火狐没事。</p></li><li class=\"reply\" data-id=884><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 12:49</span></p><p class=\"reply-content\">...那是什么 , 广告拦截吗~</p></li><li class=\"reply\" data-id=878><p class=\"reply-info\"><a href=\"/user/profile?userId=983\">孟浩</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 08:06</span></p><p class=\"reply-content\">我也报这个错, 你的pop.js被adblock屏蔽了, 改个名就好了</p></li><li class=\"reply\" data-id=864><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 16:11', '0', '8836', '2016-04-13 13:53:00'), ('105', null, '梁誉译', null, '并不是火狐的问题或者代码的问题，我火狐没事。', '104', null, '2016-04-14 20:28:00'), ('106', null, '陈慕杰', null, '...那是什么 , 广告拦截吗~', '104', null, '2016-04-14 12:49:00'), ('107', null, '孟浩', null, '我也报这个错, 你的pop.js被adblock屏蔽了, 改个名就好了', '104', null, '2016-04-14 08:06:00'), ('108', null, '陈慕杰', null, '这不是代码问题吧,,,JS文件没载进来, 就你会的样子', '104', null, '2016-04-13 16:11:00'), ('109', 'XD微客', '卢睿韬', '8', '学习了，不过第一行好像多了个分号。。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=857><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 12:41', '0', '8836', '2016-04-13 10:48:00'), ('110', null, '陈慕杰', null, '谢谢review~~<br />\r\n分号是为了应对代码合并压缩时，由于缺少分号；带来的错误。', '109', null, '2016-04-13 12:41:00'), ('111', 'sunshine', '向王涛', '7', '学习到了，学到一个封装思路</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=842><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-12 14:33', '0', '8836', '2016-04-12 13:39:00'), ('112', null, '陈慕杰', null, '谢谢review~', '111', null, '2016-04-12 14:33:00'), ('113', 'freshman', '安超', '9', '写的不错啊，其他团队都没怎么做到封装，赞一个！</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=841><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-12 14:33', '0', '8836', '2016-04-12 12:32:00'), ('114', null, '陈慕杰', null, '谢谢review~', '113', null, '2016-04-12 14:33:00'), ('115', 'Fenomenix', '肖新宇', '8', '还不错</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=838><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-12 09:28', '0', '8836', '2016-04-12 09:27:00'), ('116', null, '陈慕杰', null, '谢谢OvO~', '115', null, '2016-04-12 09:28:00'), ('117', 'IFE.FrontEnd', '陈慕杰', '9', '使用了react，还有一些自动化工程来完成是个不错的想法，但是反而使用了这么多东西才显得臃肿，甚至想找到你配置这种东西的JS都看不到，还是说压根就没有提供使用JavaScript控制浮出层展现和关闭的接口(#任务需求六)。而且完成的功能却少之又少，没有实现拖拽功能，按钮回调的参数也不知道有没有。姑且亮点？就是使用了react还有出现的动画吧。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=922><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 17:16</span></p><p class=\"reply-content\">在组件上配置就是React的特点。你可以去看下蚂蚁金服他们做的ant-design，使用组件的时候也是这样的。</p></li><li class=\"reply\" data-id=921><p class=\"reply-info\"><a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 16:58</span></p><p class=\"reply-content\">额..二十分钟前提交的,,用的是html5的drag和drop,挺好的, 但是最好还是实现跟随鼠标的拖拽吧, 效果还是好看一点的..<br />\r\n关于配置我觉得任务需求上说的是提供接口配置,  而不是在组件上直接配置</p></li><li class=\"reply\" data-id=919><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 16:44</span></p><p class=\"reply-content\">还有你说的找不到配置的JS的问题。React里配置组件是通过往组件中传入Props来做到的，你可以在App.jsx里找到Dialog组件的配置。</p></li><li class=\"reply\" data-id=918><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 16:39', '0', '9157', '2016-04-16 13:11:00'), ('118', null, '颜适', null, '在组件上配置就是React的特点。你可以去看下蚂蚁金服他们做的ant-design，使用组件的时候也是这样的。', '117', null, '2016-04-16 17:16:00'), ('119', null, '陈慕杰', null, '额..二十分钟前提交的,,用的是html5的drag和drop,挺好的, 但是最好还是实现跟随鼠标的拖拽吧, 效果还是好看一点的..<br />\r\n关于配置我觉得任务需求上说的是提供接口配置,  而不是在组件上直接配置', '117', null, '2016-04-16 16:58:00'), ('120', null, '颜适', null, '还有你说的找不到配置的JS的问题。React里配置组件是通过往组件中传入Props来做到的，你可以在App.jsx里找到Dialog组件的配置。', '117', null, '2016-04-16 16:44:00'), ('121', null, '颜适', null, 'React+Redux是为了阶段四的RIA做准备，你可以看看任务介绍。<br />\r\n你可能不太了解Redux，控制浮出层的展现和关闭，只需要调用Action里的alertDialog和hideDialog就行了。这会返回一个Action对象给Reducer处理展现和关闭的功能。<br />\r\n拖拽我写了啊…………', '117', null, '2016-04-16 16:39:00'), ('122', '五好青年', '高世超', '7', '效果不错,动画值得借鉴,拖拽的样式挺好的,但是你代码地址其他东西太多,最好把你自己写的代码独立出来 附到代码地址上就好了,方便看源码..唯一有点不足就是浮出层出来后,没法页面没法滚动了!</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=931><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-17 15:54', '0', '9157', '2016-04-17 14:42:00'), ('123', null, '颜适', null, '浮出层禁止滚动是任务要求。代码地址里src下的都是源代码呀，build是打包后的。', '122', null, '2016-04-17 15:54:00'), ('124', '精通JavaScript的拼写', '朱巧', '7', '有个bug额~  下面的遮罩层不能把整个页面遮全。<br />\r\n这个动画效果不错，喜欢</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=905><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-15 15:40</span></p><p class=\"reply-content\">Bug修好啦！把遮罩层和浮动框的定位换成fixed就好了，之前疏忽了。还发现了一个附带的Bug，就是原来的动画起始位置是定死的，没有随着滚动条的变化而变化，所以当按钮相对viewport的位置变了的时候就会出Bug，顺带也修好了。再次感谢！</p></li><li class=\"reply\" data-id=904><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-15 15:25', '0', '9157', '2016-04-15 15:13:00'), ('125', null, '颜适', null, 'Bug修好啦！把遮罩层和浮动框的定位换成fixed就好了，之前疏忽了。还发现了一个附带的Bug，就是原来的动画起始位置是定死的，没有随着滚动条的变化而变化，所以当按钮相对viewport的位置变了的时候就会出Bug，顺带也修好了。再次感谢！', '124', null, '2016-04-15 15:40:00'), ('126', null, '颜适', null, '多谢提醒。不在页面顶端的时候遮罩层确实会遮不全，这个Bug太隐蔽了，我马上Fix。', '124', null, '2016-04-15 15:25:00'), ('127', 'Stack', '陈维晟', '8', '不错 我也是想看这个拖拽怎么处理 <br />\r\n 我自己用js写的拖拽鼠标移动速度太快 就会跟不上。。。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=930><p class=\"reply-info\"><a href=\"/user/profile?userId=7182\">高世超</a>&nbsp;回复&nbsp;<span class=\"time\">04-17 15:05', '0', '9157', '2016-04-14 13:52:00'), ('128', null, '高世超', null, '你把鼠标移动事件和鼠标松开事件写到document中就不会出现跟不上的问题了', '127', null, '2016-04-17 15:05:00'), ('129', 'Lafairy', '龚沛', '8', '6666 一整套前端自动化好评~想看看用react怎么处理拖拽 另外似乎没有做移动端兼容？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=885><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7019\">颜适</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 13:33', '0', '9157', '2016-04-14 12:04:00'), ('130', null, '颜适', null, '移动端兼容没写过诶，还要去读一下人家的源码呢。', '129', null, '2016-04-14 13:33:00'), ('131', 'AlphaRun', '颜适', '6', '最大的问题是点击遮罩层时浮动框没有消失，这一点造成用户非常比较差。<br />\r\n拖拽做得挺好的。拉伸有点小问题，一般拉伸是拉开内容区的，而不是页脚，不过这倒是小问题，因为从需求上讲，很少需要可拉伸的浮动框的。<br />\r\n代码上，操作DOM那部分写得太繁琐了，建议直接拼接字符串，innerHTML比多个createElement效率更高。再考虑写成MVC吧，对后续有很大帮助的。<br />\r\n最后，求互review啦。<br />\r\nhttp://ife.baidu.com/review/detail?workId=9157</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=910><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7182\">高世超</a>&nbsp;回复&nbsp;<span class=\"time\">04-15 17:10', '0', '8864', '2016-04-15 15:16:00'), ('132', null, '高世超', null, '操作DOM我也感觉写的挺麻烦的，最近看javascript设计模式 有个简单模版模式，能快速创建dom 不过没怎么看懂， 点击浮出层以外的地方关闭浮出层功能已完善，拉伸效果也已修改（拉伸只改变内容区，不改变页脚区）！ 谢谢你的review！', '131', null, '2016-04-15 17:10:00'), ('133', '魑魅魍魉', '郑旭', '8', '那个按钮明明是极客学院的里的实战，名字叫做幽灵按钮！<br />\r\n我觉得吧缺少一个任务里要求的，点击浮层以外的地方关闭浮层功能！</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=906><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7182\">高世超</a>&nbsp;回复&nbsp;<span class=\"time\">04-15 16:45', '0', '8864', '2016-04-15 14:37:00'), ('134', null, '高世超', null, '没注意到这个要求，已经修改好了 ，谢谢你的review', '133', null, '2016-04-15 16:45:00'), ('135', 'Sublime Us', '杨军', '8', '动画是在慕课网学的吧，嘿嘿', '0', '8864', '2016-04-15 08:34:00'), ('136', 'Fenomenix', '肖新宇', '9', '我要好好研究一下那个动画了， 真有趣</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=897><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7182\">高世超</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 20:29', '0', '8864', '2016-04-14 15:37:00'), ('137', null, '高世超', null, '(⊙o⊙)… 你忽略了重点', '136', null, '2016-04-14 20:29:00'), ('138', 'web轮子', '赵汉', '8', '请问<br />\r\npop.style.left=event.clientX+250-disX+&#039;px&#039;;<br />\r\npop.style.top=event.clientY+125-disY+&#039;px&#039;;<br />\r\n如果不加250，125，只写clientX-disX我觉得这样没错呀，可是为什么会出现，一拖动窗口会先向左向上移动一下呢，不明白为什么会出现这个现象</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=924><p class=\"reply-info\">作者<a href=\"/user/profile?userId=48\">赵宇翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 17:29', '0', '8826', '2016-04-16 10:49:00'), ('139', null, '赵宇翔', null, '因为我的浮出层在窗口垂直居中运用了负外边距，所以在拖拽时要加上相应的数值来抵消。', '138', null, '2016-04-16 17:29:00'), ('140', 'IFE.FrontEnd', '陈慕杰', '8', '任务要求基本实现了，但是样式的确该美化一下，还有豪迈的字体', '0', '8826', '2016-04-12 08:48:00'), ('141', '前端小分队2016', '郭文质', '6', '只能说大叔太豪迈了，建议美化一下，另外一个遮罩和框框用了5个id确实有点多了', '0', '8826', '2016-04-11 18:19:00'), ('142', 'Big Front End', '张鑫淼', '8', '实现了拖动的功能，学习到了~', '0', '8826', '2016-04-11 18:04:00'), ('143', 'AlphaRun', '颜适', '6', 'DOCTYPE没用新标准写。<br />\r\nCSS和JS也不要直接写在标签里。<br />\r\n效果比较粗糙，可以考虑加动画。', '0', '9168', '2016-04-16 11:38:00'), ('144', 'Code Baby', '卢雅', '9', '做得挺不错的，基本的要求已经做到了，请问楼主，这个弹出层的效果是怎么实现的呢', '0', '9168', '2016-04-16 09:27:00'), ('145', 'W3Cfuns', '侯晓行', '8', '就是js里的样式太多了', '0', '9138', '2016-05-05 14:03:00'), ('146', 'KTW', '任远', '9', '学会了在JS里写CSS。。。感恩有你。。。', '0', '9138', '2016-05-04 19:21:00'), ('147', '春暖花开队', '龚苗', '9', 'OOP?', '0', '9138', '2016-05-04 17:15:00'), ('148', 'imooc-慕星人', '阮小泽', '10', '太牛了！', '0', '9138', '2016-04-29 22:46:00'), ('149', '对对队', '张奇琦', '9', '挺好的，只有一个建议，就是把css和js分开，用js的添加className方法给元素设定样式</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1011><p class=\"reply-info\">作者<a href=\"/user/profile?userId=10454\">孟浩</a>&nbsp;回复&nbsp;<span class=\"time\">04-25 13:49', '0', '10820', '2016-04-25 08:13:00'), ('150', null, '孟浩', null, '恩', '149', null, '2016-04-25 13:49:00'), ('151', '浮生若梦', '彭漫', '9', '看了你的代码，学习到封装。我有一个地方不是很明白，你的TableTool为什么要写成立即执行函数</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1005><p class=\"reply-info\">作者<a href=\"/user/profile?userId=607\">张磊磊</a>&nbsp;回复&nbsp;<span class=\"time\">04-25 10:16', '0', '9583', '2016-04-25 08:50:00'), ('152', null, '张磊磊', null, '因为使用立即执行函数，可以做到函数私有空间。函数当中的方法只能通过你return出的接口去暴露，增加了封装性', '151', null, '2016-04-25 10:16:00'), ('153', '前端刷刷队', '张圣兰', '9', '就喜欢这种能开阔我眼界的代码，原来组件是这么玩的', '0', '9583', '2016-04-24 17:55:00'), ('154', 'code matters', '彭君怡', '9', '其实我也觉得封装的很漂亮，以前一直处于很入门的状态，也搞不清封住的涵义，看了你的之后明白的封装的艺术，真的很好看，逻辑方面其实应该还是有可以加强的，鄙人眼拙，正在参照学习封装的艺术，所以在BUG方面没能有太大的建议了~</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=982><p class=\"reply-info\">作者<a href=\"/user/profile?userId=607\">张磊磊</a>&nbsp;回复&nbsp;<span class=\"time\">04-22 15:33', '0', '9583', '2016-04-21 23:33:00'), ('155', null, '张磊磊', null, '我也是第一次做组件化，对各种设计模式都不是很熟悉呢~为了写出更好的代码努力吧~', '154', null, '2016-04-22 15:33:00'), ('156', '江安职业高中', '梁誉译', '7', '代码不够简练，封装做得不错，比较函数最好通过接口给出来，有个默认的。不然类似名字的这种或者格式不是数字的就没办法比较。或者是工资之类的￥1000这种字符串就没办法比较。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=986><p class=\"reply-info\"><a href=\"/user/profile?userId=8861\">梁誉译</a>&nbsp;回复&nbsp;<span class=\"time\">04-22 23:18</span></p><p class=\"reply-content\">我是想说将比较函数做一个接口，默认是数字排序，让组件的使用者自己做比较函数的实现</p></li><li class=\"reply\" data-id=981><p class=\"reply-info\">作者<a href=\"/user/profile?userId=607\">张磊磊</a>&nbsp;回复&nbsp;<span class=\"time\">04-22 15:32', '0', '9583', '2016-04-21 20:45:00'), ('157', null, '梁誉译', null, '我是想说将比较函数做一个接口，默认是数字排序，让组件的使用者自己做比较函数的实现', '156', null, '2016-04-22 23:18:00'), ('158', null, '张磊磊', null, '本来想拓展做unicode的  姓名首字母排序，但是拓展太多怕收不住手···已经拓展很多 要把自己玩死了 - -（自己本身是做产品的，要把自己弄死了，然后想想就收手了），想要拓展的话可以适当改下sort函数哦  进行个判断，自己DIY一下吧~', '156', null, '2016-04-22 15:32:00'), ('159', 'We Are The World', '陈伟钰', '8', '各方面相当不错，介绍也很详细。就说说我发现的问题吧：<br />\r\n1，姓名排序，给出了排序标识图片，却不能排序。要排的话应该是按拼音排序。还有，建议不只是给出是否能排序接口，最好能给出哪几列能排序的接口。<br />\r\n2，既然你给出了容器接口，发现一个BUG，当容器存在padding-top，或者margin-top 的话（例如.table3 { padding:300px 0 0 0 }），首行冻结就跪了。<br />\r\nPS：不嫌弃的话可以来review下我们组（http://ife.baidu.com/review/detail?workId=9738）</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=983><p class=\"reply-info\">作者<a href=\"/user/profile?userId=607\">张磊磊</a>&nbsp;回复&nbsp;<span class=\"time\">04-22 15:45', '0', '9583', '2016-04-20 14:54:00'), ('160', null, '张磊磊', null, '感谢review··这个BUG大概是我的冻结逻辑写的不够紧密吧', '159', null, '2016-04-22 15:45:00'), ('161', '敌后武工队', '冯泽民', '10', '小子我看好你噢~', '0', '9583', '2016-04-18 22:52:00'), ('162', 'BigFE', '胡春望', '9', '学习了', '0', '8886', '2016-04-27 16:12:00'), ('163', '江安职业高中', '梁誉译', '8', '感觉这个是我看过的最喜欢的代码了，封装也有，代码也够简练，功能也实现了。学习了。', '0', '8886', '2016-04-22 23:26:00'), ('164', 'KTW', '任远', '8', '代码风格很棒，我还是要学习一个！', '0', '9178', '2016-05-04 19:19:00'), ('165', '对对队', '张奇琦', '9', '逻辑简单，不明白为啥别人要搞的模块化那么复杂= =<br />\r\n一个小建议，19行innerHTML那个放在for循环外面，否则会影响性能', '0', '8912', '2016-04-23 21:07:00'), ('166', '江安职业高中', '梁誉译', '6', '没有将组件封装，代码比较简练，做了一定了兼容处理。', '0', '8912', '2016-04-22 23:16:00'), ('167', 'Go', '孔阿栋', '8', '一直在纠结如何对对象属性进行排序，想了很复杂的方法，看了你的sort豁然开朗。', '0', '8912', '2016-04-13 15:16:00'), ('168', '学霸咚霸chua', '罗小川', '7', '实现的挺不错的~小工具库竟然用下划线。_.看的有些别扭,用$是不是更好些', '0', '8994', '2016-04-14 16:02:00'), ('169', '海贼兄弟', '孟浩', '7', '就是耦合度比较高。', '0', '10807', '2016-04-25 18:09:00'), ('170', 'ZLFE', '张晶尧', '8', '很厉害，我也得回去写个mini-query。<br />\r\n通过属性查找那里有意思。学习了。<br />\r\n另外，sort排序函数，用三元选择符比直接用减法有什么优势么？<br />\r\n是因为减法如果返回值是0的时候，sort排序的问题么呢？但我认为相等就不需要排了啊。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=831><p class=\"reply-info\">作者<a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">04-11 21:06', '0', '8844', '2016-04-11 20:39:00'), ('171', null, '周天树', null, '谢谢你的review，也是过奖了/w<br />\r\n三元选择符那里其实并没有关系，只是我当时写的时候觉得写成&quot;if(...) {return ...} else return ...&quot;的样子不是很好看，所以就用了三元选择符。写的时候可以自行选择啦~', '170', null, '2016-04-11 21:06:00'), ('172', '进击的前端菜鸟', '李振唐', '5', '感觉数据和DOM操作糅合太紧了，也不能配置可排序列。另外建议使用innerHTML取代createElement以简化代码和提升效率', '0', '8844', '2016-04-16 08:23:00'), ('173', '前端第七班', '吕晓军', '5', '好像只提供了数据的接口', '0', '8844', '2016-04-14 09:46:00'), ('174', 'We Are The World', '李言', '6', '封装组件化不够，对题目要求抽象不够，注意力更多地集中在了参考设计图上。<br />\r\n任务要求不达标：<br />\r\n1.可以配置对哪些列支持排序功能，<br />\r\n2.提供点击排序按钮后的响应接口。<br />\r\n待改进。', '0', '8844', '2016-04-12 20:22:00'), ('175', '棉花糖', '杨健', '8', '暴力膜不可取', '0', '8844', '2016-04-12 16:01:00'), ('176', '我的队', '关基达', '8', '总体不借，数据的注入方式有点生硬，不是很方便和全面，</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=840><p class=\"reply-info\">作者<a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">04-12 13:05', '0', '8844', '2016-04-12 12:14:00'), ('177', null, '周天树', null, '谢谢review！！', '176', null, '2016-04-12 13:05:00'), ('178', 'Big Front End', '张鑫淼', '7', '个人建议排序功能可以实现升序和降序两部分', '0', '8849', '2016-04-12 14:54:00'), ('179', '我的队', '关基达', '5', '不是很组件化，数据与表现层没有分离，只对html中的table封装了排序功能，组件化应该是提供一个组件（表现层），然后支持数据（数据层）的注入', '0', '8849', '2016-04-12 12:08:00'), ('180', 'KTW', '任远', '9', '为什么都不懂生命的珍贵……', '0', '9586', '2016-05-05 01:54:00'), ('181', '学好了去实习', '张帅', '9', '浏览器缩小的时候，标题栏宽度出现了bug', '0', '9586', '2016-05-01 12:40:00'), ('182', '调皮鬼与玩偶', '唐叶', '9', '代码写得很好，自愧不如，学习了很多，就是代码显得有点零乱，略有冗余', '0', '9586', '2016-04-29 10:17:00'), ('183', '小攻城狮', '姚晨嵩', '9', '首行悬浮的时候点击排序按钮 首行就不悬浮了。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1007><p class=\"reply-info\">作者<a href=\"/user/profile?userId=607\">张磊磊</a>&nbsp;回复&nbsp;<span class=\"time\">04-25 10:19</span></p><p class=\"reply-content\">国会上传</p></li><li class=\"reply\" data-id=1006><p class=\"reply-info\">作者<a href=\"/user/profile?userId=607\">张磊磊</a>&nbsp;回复&nbsp;<span class=\"time\">04-25 10:19', '0', '9586', '2016-04-24 20:35:00'), ('184', null, '张磊磊', null, '国会上传', '183', null, '2016-04-25 10:19:00'), ('185', null, '张磊磊', null, 'BUG已经修复，因为冻结首行是基于滚动条的事件监听，排序后会重新渲染表格没有移动滚动条就不会调用冻结，只要加个初始状态判断就可修复了', '183', null, '2016-04-25 10:19:00'), ('186', '敌后武工队', '冯泽民', '10', '完全理解了 面向对象 真的很不错 加油啊~', '0', '9586', '2016-04-18 22:50:00'), ('187', '打怪升级', '孙广东', '7', '这样会有问题的，你的单元格宽度是固定的，如果不固定的话，tbody中单元格的宽度可能会变小', '0', '9190', '2016-05-20 00:04:00'), ('188', '康村前端小分队', '李瑗瑗', '9', '实现的想当简洁！值得学习！', '0', '9190', '2016-05-02 02:44:00'), ('189', '进击的前端菜鸟', '李振唐', '7', '你是把表头行给摘下来设成fixed吧，这样有点问题就是触发事件的那一瞬间，表格数据的第一行会立刻递补表头的位置，导致第一行数据直接“跳上去”看不见了。所以我选择了复制一个表头，让原来的表头继续占位。不知道有没有更好的方案</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=909><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7182\">高世超</a>&nbsp;回复&nbsp;<span class=\"time\">04-15 16:58', '0', '9137', '2016-04-15 16:25:00'), ('190', null, '高世超', null, '嗯，我也刚刚发现表格数据会往上递补。不仔细看还真发现不出来！<br />\r\n谢谢你的review', '189', null, '2016-04-15 16:58:00'), ('191', '艾欧尼亚', '胡江', '7', '封装性很好，关键是顶部没对齐呀，往上拉之后明显往右移动了一点距离，Chrome下测试</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=900><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7182\">高世超</a>&nbsp;回复&nbsp;<span class=\"time\">04-15 00:04', '0', '9137', '2016-04-14 22:26:00'), ('192', null, '高世超', null, '谢谢，我都没注意到，还是你观察仔细，我已经修改完了，明天上传！', '191', null, '2016-04-15 00:04:00'), ('193', 'Fenomenix', '肖新宇', '8', '不错， 值得学习， 我做的真是太粗糙了</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=898><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7182\">高世超</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 20:31', '0', '9137', '2016-04-14 15:36:00'), ('194', null, '高世超', null, 'O(∩_∩)O谢谢你 review', '193', null, '2016-04-14 20:31:00'), ('195', '艾欧尼亚', '胡江', '7', '需求实现不错，代码封装性有待加强，review了其他人的代码，就你实现了顶部对齐，其他人都没对齐。', '0', '8869', '2016-04-14 22:25:00'), ('196', '五好青年', '高世超', '7', '你的reset.css 值得学习,还从来没想过弄个基础css来, 你的reset.css 解决了我首行固定导致固定行发生变化的bug', '0', '8869', '2016-04-13 20:12:00'), ('197', 'IFE.FrontEnd', '陈慕杰', '7', '固定的首行脱离后没有和表格对齐。。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=837><p class=\"reply-info\">作者<a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">04-12 09:16</span></p><p class=\"reply-content\">其实这个bug倒还不是主要的缺陷 。。。主要的缺陷是我觉得我的实现方法比较傻而且也想不到什么更好的实现方法。。。</p></li><li class=\"reply\" data-id=836><p class=\"reply-info\">作者<a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">04-12 09:12', '0', '8869', '2016-04-12 08:52:00'), ('198', null, '周天树', null, '其实这个bug倒还不是主要的缺陷 。。。主要的缺陷是我觉得我的实现方法比较傻而且也想不到什么更好的实现方法。。。', '197', null, '2016-04-12 09:16:00'), ('199', null, '周天树', null, '昨晚做完就没管了。。。刚刚fix了这个bug，现在应该好了。。。', '197', null, '2016-04-12 09:12:00'), ('200', '无用改革', '刘夏', '7', '挺棒的，楼上说的对，判断滚动那部分，最好不用特定的数值，否则需求一变改动成本就高了', '0', '10808', '2016-05-01 15:23:00'), ('201', '海贼兄弟', '孟浩', '7', '单说冻结行这部分。监听mousewheel事件不太好，用上下键或者鼠标拖滚动条也能滚动页面。写了个常数110不好，110应该是offsetTop吧。', '0', '10808', '2016-04-25 18:14:00'), ('202', '进击的前端菜鸟', '李振唐', '6', '思路值得借鉴；代码太散了，没有封装性可言，到下个阶段怎么拿出来用呢</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=891><p class=\"reply-info\">作者<a href=\"/user/profile?userId=48\">赵宇翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 17:20', '0', '9099', '2016-04-14 11:53:00'), ('203', null, '赵宇翔', null, '这。。。如何封装，总共才10+行，而且只针对thead一个元素，后期也可以随意使用', '202', null, '2016-04-14 17:20:00'), ('204', '前端初学分队', '周福林', '6', '到顶后表格没有对齐，第二行会突然移动到第一行下面。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=881><p class=\"reply-info\">作者<a href=\"/user/profile?userId=48\">赵宇翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 09:40', '0', '9099', '2016-04-13 22:32:00'), ('205', null, '赵宇翔', null, '什么浏览器，我用chrome，firefox，IE下都没有这个问题。我又增强了一下兼容性，你再试试。', '204', null, '2016-04-14 09:40:00'), ('206', '对对队', '张奇琦', '9', '默认6行tr，但是有的月份只需要5行，我也没有好办法解决</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1064><p class=\"reply-info\">作者<a href=\"/user/profile?userId=10454\">孟浩</a>&nbsp;回复&nbsp;<span class=\"time\">05-03 21:56', '0', '10853', '2016-05-02 19:28:00'), ('207', null, '孟浩', null, '也能解决的，我就是图省事', '206', null, '2016-05-03 21:56:00'), ('208', '调皮鬼与玩偶', '唐叶', '8', '代码很简洁，思路也很清晰，如果能加上封装和设计模式就再好不过了。', '0', '9319', '2016-04-29 12:04:00'), ('209', 'Web前端设计', '张恒', '8', '1.demo不能看，你可以用 https://rawgit.com 这个网站，把你的github的地址粘上去，就能看了<br />\r\n2.你的代码用了对象，原型，很方便。那块我不知道说的对不对，我目前正学，不做评价<br />\r\n3.样式不错 向你学习，还补充了前后月的剩余天数<br />\r\n4.小小瑕疵，不知是否是你忘了，没有高亮当前日期。<br />\r\n5.总体完美，那个样式最好还是写在外头吧，写在html中，将来修改时不方便<br />\r\n本人小白，水平有限，望谅解，欢迎互评！<br />\r\n3.</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1046><p class=\"reply-info\">作者<a href=\"/user/profile?userId=6648\">张圣兰</a>&nbsp;回复&nbsp;<span class=\"time\">04-29 09:57', '0', '10867', '2016-04-28 14:53:00'), ('210', null, '张圣兰', null, '1. demo不能看，是因为我把仓库的文件名改了，百度任务这边忘了同步更新了，不过你给的github预览网站意外的好用呢<br />\r\n1. 关于使用对象，我也是刚学这个，说实话，暂时没觉得有多好，只是感觉很高级的样子。', '209', null, '2016-04-29 09:57:00'), ('211', '组团来卖萌的', '周天树', '7', '题主更新了，因此修改评分。。。看了下代码，组件化，封装程度还可以再改进改进，总体还可以</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=863><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3225\">刘建环</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 15:56</span></p><p class=\"reply-content\">这个写的时候就实现为：当点击了某一天的时候，将当前的年月日获取到后并赋值给input当中，然后将日历面板收起来。。多谢详细的review</p></li><li class=\"reply\" data-id=862><p class=\"reply-info\"><a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 15:44</span></p><p class=\"reply-content\">嗯，基本解决bug了，不过貌似依然不能具体选择某一天，点击日历中具体的某一天后日历面板依然会自动消失，不过这个解决起来应该挺快的</p></li><li class=\"reply\" data-id=861><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3225\">刘建环</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 15:27</span></p><p class=\"reply-content\">不好意思哈。去吃饭前急着上传了弄错了版本啦。。谢谢，谢谢，多谢及时的review，已更新</p></li><li class=\"reply\" data-id=859><p class=\"reply-info\"><a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 14:47', '0', '9073', '2016-04-13 14:37:00'), ('212', null, '刘建环', null, '这个写的时候就实现为：当点击了某一天的时候，将当前的年月日获取到后并赋值给input当中，然后将日历面板收起来。。多谢详细的review', '211', null, '2016-04-13 15:56:00'), ('213', null, '周天树', null, '嗯，基本解决bug了，不过貌似依然不能具体选择某一天，点击日历中具体的某一天后日历面板依然会自动消失，不过这个解决起来应该挺快的', '211', null, '2016-04-13 15:44:00'), ('214', null, '刘建环', null, '不好意思哈。去吃饭前急着上传了弄错了版本啦。。谢谢，谢谢，多谢及时的review，已更新', '211', null, '2016-04-13 15:27:00'), ('215', null, '周天树', null, '还有，你的代码地址链接也出问题了，我看了一下原因是你后来又改过你git里仓库的东西了，记得把代码地址也改一下啊', '211', null, '2016-04-13 14:47:00'), ('216', '前端小白学习', '刘洋', '7', '发现一个bug，点出日历后，调节浏览器窗口大小，日历停止不动，并不会跟随文本输入框移动。<br />\r\n<br />\r\n我也不知道怎么改。', '0', '9302', '2016-05-08 19:32:00'), ('217', '前端刷刷队', '张圣兰', '6', '看到你用 table 来绘制日期小方块，才想起来我用 flex 排列小方块，存在组件和 css 的耦合问题。<br />\r\n发现两个问题：<br />\r\n1. 即时选中了日期后，另外一个日期方块还是标红的。<br />\r\n2. 没有给出限制时间跨度的接口。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1037><p class=\"reply-info\">作者<a href=\"/user/profile?userId=4715\">张恒</a>&nbsp;回复&nbsp;<span class=\"time\">04-28 07:45', '0', '10859', '2016-04-27 21:14:00'), ('218', null, '张恒', null, '谢谢评价，我会尽快修改，', '217', null, '2016-04-28 07:45:00'), ('219', '前端路上', '麦尔丹', '7', '基本功能是按照题目要求是实现的很好,但总感觉外观不是很看哟', '0', '9335', '2016-05-18 18:47:00'), ('220', '前端小分队', '孔磊', '8', '赞，水平有限，打个分鼓励下。', '0', '9335', '2016-05-04 16:31:00'), ('221', 'AlphaRun', '颜适', '5', 'Bug颇多。<br />\r\n1. 点击灰色部分不能自动跳转上个月或下个月。<br />\r\n2. 点击31号后，切换到不足31天的月份不会自动选中30号。<br />\r\n3. 2015年2月份，应该是第一行和最后一行灰色，而不是最后两行。<br />\r\n4. 设置日期时，假如当前为2020年，删除0后就有弹出框，而没有等我完整输入年份，交互逻辑不合理。又比如在20时，输入2，也会有弹出框。<br />\r\n5. 输入月份和日期超出合理范围时，不会报错，反而会出现日期标示不正确的问题。如2000年1月32日，2020年2月31日，2020年13月31日等。<br />\r\n6. 看了源代码，日期上下限只取了年份，而没有限制在具体的日期。<br />\r\n7. 选中日期或点击空白位置后，日期框不会消失。<br />\r\n8. 鼠标hover的样式不对。左右箭头处，会出现明明是pointer，点击后却没有点中的情况。hover星期栏的时候也不应该是pointer样式。hover空白处也会不合理地变成pointer。<br />\r\n<br />\r\n欢迎互评。http://ife.baidu.com/review/detail?workId=10803', '0', '10857', '2016-04-27 12:01:00'), ('222', 'KTW', '任远', '10', '学习一下', '0', '10869', '2016-05-05 11:28:00'), ('223', '对对队', '张奇琦', '9', '没什么问题<br />\r\n比我的整洁太多，虽然不知道你这叫什么模式，但是感觉挺好的<br />\r\n加一个点击mask则日历框会消失的操作吧，我能想到的都是UI问题</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1065><p class=\"reply-info\">作者<a href=\"/user/profile?userId=10454\">孟浩</a>&nbsp;回复&nbsp;<span class=\"time\">05-03 22:01', '0', '10869', '2016-05-02 19:51:00'), ('224', null, '孟浩', null, '我看到任务51里说，封装的话样式最好单独写css，比如这个我应该写 datePicker.css 然后在js里直接addClass。其实我想过，我主要考虑到这样会占用css的class的名字，有可能写多了会重复，而且要多写个link。不过之后我就会把新样式分开写了。', '223', null, '2016-05-03 22:01:00'), ('225', '前端小分队2016', '郭文质', '8', '请教一下这个demo地址是怎么得来的？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1038><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7182\">高世超</a>&nbsp;回复&nbsp;<span class=\"time\">04-28 08:49', '0', '9551', '2016-04-26 11:01:00'), ('226', null, '高世超', null, '打开你的工程-Settings-Launch automatic page generator-continue to layouts<br />\r\n然后 你把你的做的东西复制到gh-pages 分支中 然后 用户名.github.io/工程名/....<br />\r\n今天刚注意到!不好意思回复晚了', '225', null, '2016-04-28 08:49:00'), ('227', '前端刷刷队', '张圣兰', '8', '用数组记录 html 元素，在用 join 一次性 DOM 操作，比用 string优雅一些，学习到了。', '0', '9400', '2016-04-30 16:04:00'), ('228', '进击的前端菜鸟', '李振唐', '7', '1.可以跨月赞，不过用两个表格的话，如何特殊显示被选中的时间段？<br />\r\n2.一个bug：禁选日期可以被选在时间段内</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=987><p class=\"reply-info\">作者<a href=\"/user/profile?userId=455\">胡江</a>&nbsp;回复&nbsp;<span class=\"time\">04-22 23:28', '0', '9400', '2016-04-19 18:04:00'), ('229', null, '胡江', null, '谢谢review<br />\r\n1、特殊显示被选中时间段未被考虑，有待添加<br />\r\n2、并未设置禁选日期这一功能', '228', null, '2016-04-22 23:28:00'), ('230', '前端刷刷队', '张圣兰', '7', '输入框的月份数字是错的。', '0', '10827', '2016-04-28 14:09:00'), ('231', null, null, '6', '既然是组件的话css就不应该再html里面直接写了<br />\r\n配置里面id不应该让用户去配置<br />\r\n加载时触发click事件这个也没有必要<br />\r\n组件的架构也有问题，只能在input上绑定是不合理的<br />\r\n很多逻辑不够优雅', '0', '10863', '2016-05-03 19:13:00'), ('232', '进击的前端菜鸟', '李振唐', '5', '1.不能跨月选时间段<br />\r\n2.在某个月选择了日期后转到另一个月，选区位置不变，有可能落在空白格<br />\r\n3.个人觉得，选中一段时间后再次单击某个日期时，应该废弃之前的选区，将点击的日期作为新的起点，因为多点几次之后可能会忘记刚才点的是起点还是重点日期<br />\r\n4.封装性不好，搞了一堆全局函数', '0', '9357', '2016-04-19 17:55:00'), ('233', null, null, '1', 'demo直接跑不起来，最开始Datepicker里面的this使用就有问题。', '0', '10900', '2016-06-15 19:12:00'), ('234', null, null, '7', '总体来说不错，但是很多长宽是用 js 计算出来的，这就导致改变浏览器宽度后，布局就乱了。建议尽可能使用 css 实现效果，如果一定要用 js，可以考虑对 resize 事件的响应。', '0', '9055', '2016-04-27 14:38:00'), ('235', 'sniper. 夜', '陈冠宇', '9', '思路清奇 代码整洁', '0', '9055', '2016-05-02 13:59:00'), ('236', 'xilixili', '朱潇然', '9', '哈哈</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1042><p class=\"reply-info\"><a href=\"/user/profile?userId=2950\">朱潇然</a>&nbsp;回复&nbsp;<span class=\"time\">04-28 17:45</span></p><p class=\"reply-content\">真的5张图片的时候，你仔细对比一下题目的图</p></li><li class=\"reply\" data-id=1041><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-28 16:28</span></p><p class=\"reply-content\">已经完全符合了呀，你确定没看错？</p></li><li class=\"reply\" data-id=1039><p class=\"reply-info\"><a href=\"/user/profile?userId=2950\">朱潇然</a>&nbsp;回复&nbsp;<span class=\"time\">04-28 10:03</span></p><p class=\"reply-content\">3张图片边长是1/2高度和五张图片边长是1/3宽度，要求正方形</p></li><li class=\"reply\" data-id=1036><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-27 23:14', '0', '9055', '2016-04-27 22:06:00'), ('237', null, '朱潇然', null, '真的5张图片的时候，你仔细对比一下题目的图', '236', null, '2016-04-28 17:45:00'), ('238', null, '丘翔', null, '已经完全符合了呀，你确定没看错？', '236', null, '2016-04-28 16:28:00'), ('239', null, '朱潇然', null, '3张图片边长是1/2高度和五张图片边长是1/3宽度，要求正方形', '236', null, '2016-04-28 10:03:00'), ('240', null, '丘翔', null, '哪个布局与题目不符呢？', '236', null, '2016-04-27 23:14:00'), ('241', '小攻城狮', '连立麟', '9', '技术代码+10，我笔记本显示屏结构会乱，另外一个大一点的显示屏是好的-1', '0', '9055', '2016-04-26 17:05:00'), ('242', '嗨！贾克斯', '余思烨', '10', '从第二阶段追到第三阶段，你的水平太高以至于我觉得自己根本review不出什么东西，甚至有时候自己做不出思路的时候都是来参考一下代码的。<br />\r\n有个疑问，我看到不少人的原型链都是一种方法一种方法的添加的xx.prototype.xx = function(){// todo}，这种和在封装的时候一次性添加原型链，即xx.prototype = { // some functions}，有什么好处吗？还是说只是为了美观好看。<br />\r\n如果有空能写个怎样使用你的库的文档就更好了，因为自己想看看库的文档怎么写会比较合适。<br />\r\n另外很好奇你平时学习的方式，最后也希望你给我的代码提点意见，谢谢你</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=963><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 14:59</span></p><p class=\"reply-content\">汇编是真简单，然而写起来是真麻烦，毕竟计算机专业，汇编是肯定要学的。<br />\r\n<br />\r\n考虑到有前端的基础，从 nodejs 切入后端还是不错的。其次建议 php 和 python，php 是我最早接触的后端语言，从个人喜好来说，我并不是很喜欢 php，但你不得不承认 php 是现在最主流的后端语言之一，技术、框架沉淀很深。我喜欢 python 简洁的语法，用 flask 做过一些项目，因为灵活，容易扩展，很多创业公司都用 python。java 是个啰嗦的语言，写 web 也麻烦，除非有必要，不然我是不会碰 java 的。c# 在学校帮老师做项目的时候用过几次，c# mvc 很棒，可惜，我更喜欢 linux，后来就没再用过。ruby 和 go 都没有用过，在国内用得不是很多，但广受赞誉，如果有必要我倒不介意去用。<br />\r\n<br />\r\n我觉得不同的语言都有各自适用的场景，而且是相互交叉的，在具备完整的理论知识体系以及一定技术累计之后，切换语言和平台不应该存在太大问题。于是对于你提到的这几个语言，我有个人的喜好，但却也不会排斥。<br />\r\n<br />\r\n如果实在不知道选什么，就 nodejs 吧。</p></li><li class=\"reply\" data-id=961><p class=\"reply-info\"><a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 14:03</span></p><p class=\"reply-content\">另外，学汇编是真大神，那指令写出来，比C还痛苦。</p></li><li class=\"reply\" data-id=960><p class=\"reply-info\"><a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 14:01</span></p><p class=\"reply-content\">说到后端，其实对于前端来说，了解后端也是很有好处的，然而后端语言很多，php, java, python, node, ruby, go, C++等等，你觉得对于前端来说，从哪个开始会比较好呢？</p></li><li class=\"reply\" data-id=959><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 13:18', '0', '9055', '2016-04-19 10:22:00'), ('243', null, '丘翔', null, '汇编是真简单，然而写起来是真麻烦，毕竟计算机专业，汇编是肯定要学的。<br />\r\n<br />\r\n考虑到有前端的基础，从 nodejs 切入后端还是不错的。其次建议 php 和 python，php 是我最早接触的后端语言，从个人喜好来说，我并不是很喜欢 php，但你不得不承认 php 是现在最主流的后端语言之一，技术、框架沉淀很深。我喜欢 python 简洁的语法，用 flask 做过一些项目，因为灵活，容易扩展，很多创业公司都用 python。java 是个啰嗦的语言，写 web 也麻烦，除非有必要，不然我是不会碰 java 的。c# 在学校帮老师做项目的时候用过几次，c# mvc 很棒，可惜，我更喜欢 linux，后来就没再用过。ruby 和 go 都没有用过，在国内用得不是很多，但广受赞誉，如果有必要我倒不介意去用。<br />\r\n<br />\r\n我觉得不同的语言都有各自适用的场景，而且是相互交叉的，在具备完整的理论知识体系以及一定技术累计之后，切换语言和平台不应该存在太大问题。于是对于你提到的这几个语言，我有个人的喜好，但却也不会排斥。<br />\r\n<br />\r\n如果实在不知道选什么，就 nodejs 吧。', '242', null, '2016-04-19 14:59:00'), ('244', null, '余思烨', null, '另外，学汇编是真大神，那指令写出来，比C还痛苦。', '242', null, '2016-04-19 14:03:00'), ('245', null, '余思烨', null, '说到后端，其实对于前端来说，了解后端也是很有好处的，然而后端语言很多，php, java, python, node, ruby, go, C++等等，你觉得对于前端来说，从哪个开始会比较好呢？', '242', null, '2016-04-19 14:01:00'), ('246', null, '丘翔', null, '关于 prototype 的添加，无非就是，一种是增量式，一种是覆盖式，我个人倾向于增量式，绝大多数开源库代码也都用增量式写法。主要考虑到：1、增量式不会破坏原有属性；2、覆盖式会导致代码过于集中，增加不必要的缩进，不利于阅读。<br />\r\n<br />\r\n文档的话，在第四阶段的任务我会完善好。<br />\r\n<br />\r\n关于学习嘛，简单说下我的经历，我是从大一开始接触学习 c 语言，之后是汇编、c++，大学之前没有任何编程基础，一开始是挺困难的，还好，我花了大量时间来进行编程练习，通过做很多有趣的东西，一直保持着对技术的热情。到了大二，我开始接触前端，因为编程基础还可以，学起来并没有太大困难，当然，也只能做一些简单的东西。到了大三，逐渐转向后端，之后就入坑做项目了。<br />\r\n<br />\r\n最初，我的学习方式主要看书 ＋ 实践，大三之后就很少看书了，到了现在，主要的学习方式是，看文档 + 源代码 + 实践，还有偶尔关注一些技术文章和讨论。<br />\r\n<br />\r\n你们现在都挺不错的，我大三的时候就未必有你们厉害。', '242', null, '2016-04-19 13:18:00'), ('247', 'steel team', '欧阳湘粤', '8', '已经用来借鉴参考，不足之处在于滚动到最下面没有新的图片加载<br />\r\n<br />\r\n求大神拍砖 我们的代码  steel team</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=953><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 00:16', '0', '9055', '2016-04-18 16:39:00'), ('248', null, '丘翔', null, '没有加载更多图片是因为我觉得拼图布局不适合用来加载图片列表，而是用于实现特定的布局。<br />\r\n<br />\r\n我大概看了下你们的代码，问题不少，还有 bug（第一张图片出不来，看了下是 style 字符串拼接有误），这 review 得花点时间。', '247', null, '2016-04-19 00:16:00'), ('249', '五个非洲人', '李昕宇', '8', '用FF看的话，没有梯形效果</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=920><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-16 16:51</span></p><p class=\"reply-content\">其实我很早就关注了你们的代码，也有些问题想与你们讨论，后来几天忙其他事去了，现在已经 review。</p></li><li class=\"reply\" data-id=895><p class=\"reply-info\"><a href=\"/user/profile?userId=99\">李昕宇</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 20:16</span></p><p class=\"reply-content\">希望能够给我们review一下吧，五个非洲人</p></li><li class=\"reply\" data-id=875><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 21:51', '0', '9055', '2016-04-13 20:12:00'), ('250', null, '丘翔', null, '其实我很早就关注了你们的代码，也有些问题想与你们讨论，后来几天忙其他事去了，现在已经 review。', '249', null, '2016-04-16 16:51:00'), ('251', null, '李昕宇', null, '希望能够给我们review一下吧，五个非洲人', '249', null, '2016-04-14 20:16:00'), ('252', null, '丘翔', null, '上面的描述已经提到了只支持 chrome 和 safari，在 firefox 下 clip-path 必须用 url 引用 svg，暂时懒得弄了。', '249', null, '2016-04-13 21:51:00'), ('253', 'yyqqhh', '刘建环', '9', '已经实现的很不错了，但是在线地址托管在github上严重影响体验效果（你懂得）。。要么图片出不来，要么出来非常慢。。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=883><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 11:36</span></p><p class=\"reply-content\">如果是固定图片的话就这么做了，我选择用线上接口是想把这个做为第三方图片浏览网站来做，也就是，这里的图片都是动态获取过来的。</p></li><li class=\"reply\" data-id=874><p class=\"reply-info\"><a href=\"/user/profile?userId=517\">吴立宁</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 21:00</span></p><p class=\"reply-content\">图可以放在七牛</p></li><li class=\"reply\" data-id=865><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 16:30', '0', '9055', '2016-04-13 16:22:00'), ('254', null, '丘翔', null, '如果是固定图片的话就这么做了，我选择用线上接口是想把这个做为第三方图片浏览网站来做，也就是，这里的图片都是动态获取过来的。', '253', null, '2016-04-14 11:36:00'), ('255', null, '吴立宁', null, '图可以放在七牛', '253', null, '2016-04-13 21:00:00'), ('256', null, '丘翔', null, '放在 github 不是导致慢点主要的原因，关键在于图片源是 500px，接口和图片都是跨境访问，除非换国内的图片源，不过我觉得还可以容忍了，只要访问过一次图片就缓存了。', '253', null, '2016-04-13 16:30:00'), ('257', '前端泡沫组', '巫海维', '10', '代码很清晰，用了ajax还有自己的图片缓存，看完觉得自己级别还是太低... 学习了！感谢！', '0', '9055', '2016-04-13 14:08:00'), ('258', 'xuteam', '马逸东', '8', '学习了。受启发我也用的CSS实现布局，但看楼主在三个图片和五个图片的布局中都用了JS改样式。CSS3有vw和vh的长度属性直接获取视口尺寸，这样可以让JS更纯粹。<br />\r\n<br />\r\n<br />\r\n欢迎review我们组的提交', '0', '9130', '2016-04-23 14:54:00'), ('259', 'steel team', '欧阳湘粤', '8', '基本实现效果，不足在于 下拉到底部无新的图片更新，而且整体布局无居中（算是鸡蛋里挑骨头了）<br />\r\n<br />\r\nsteel team 希望邀请您来review 我们的code', '0', '9130', '2016-04-18 16:41:00'), ('260', '锟斤拷', '丘翔', '8', '总体来说做得很好，因为主要用 css 布局，js 就显得很简洁，赞一个。<br />\r\n<br />\r\n就拼图布局来说，用 css 是可以实现大部分布局的，我一开始也有考虑过要不要专注于 css。后来考虑到在我现有的知识下，有些布局还是必须用 js，同时我已经想到一个比较好的统一处理方式，就干脆用 js。<br />\r\n<br />\r\n关于 css 代码，值得一提的是用了很多 .album &gt; img:first-child:nth-last-child(n)，巧妙的对不同的 img 数量进行选择应用不同的布局。技巧是不错，但如果是我就会选择更简单的方法，根据不同的 img 数量加上不同的 class，如 3 个 img 就加上 .albim3，那么选择器就很好写了 .album3 &gt; img::nth-last-child(n)。<br />\r\n<br />\r\n就布局来说，这里实现得很好，但就图片的显示，却没有处理好。如何在一个固定区域内合理地显示一张图片可不是简单的问题。<br />\r\n<br />\r\n问题的关键在于，图片的宽高比和相框的宽高比往往是不一致的，如果强行设置图片的 width 和 height，会导致图片被拉伸，这可是图片显示的大忌。为了避免图片被拉伸，一般的做法是，对外层 div 设置 overflow: hidden，而图片本身只设置 width: 100%（图片宽高比小于相框） 或 height: 100%（图片宽高比大于相框），更完美的处理还要考虑怎么居中，而这些处理目前大都需要用 js 来完成。<br />\r\n<br />\r\n虽说 css3 已经有 object-fit: cover; 可以解决这一问题，但兼容性堪忧，目前最新的IE都是不支持的。眼下较好的方案是 bakcgound-size: cover; 著名的图片分享网站 Flickr 就是使用了这种方案。<br />\r\n<br />\r\n除了图片显示问题，相册库的封装性，也是值得讨论。<br />\r\n<br />\r\nAlbum 提供了两个初始化方法，init() 和 setUp()，仔细看了代码的实现，觉得这两个初始化方法不是很统一，同时也重复了一部分代码。<br />\r\n<br />\r\n不统一的地方在于，init 读取 data- 属性，而 setUp 通过参数传递，其实这两种方法都可以同时提供。<br />\r\n<br />\r\n重复的代码是：<br />\r\nwidth = node.dataset.width || node.clientWidth;<br />\r\nheight = node.dataset.height || node.clientHeight;<br />\r\nnode.style.width = width + &quot;px&quot;;<br />\r\nnode.style.height = height + &quot;px&quot;;<br />\r\nfixWrongStyle(node, width, height);<br />\r\n其实这段代码可以再封装成一个函数，供 init 和 setUp 调用，但似乎由于上述提到的不统一，对代码复用造成了一些影响。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=943><p class=\"reply-info\"><a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-18 00:52</span></p><p class=\"reply-content\">我看错了，原来你们是用了 object-fix: cover 的，之前是在 IE 下看到的图片被压缩，以为你们没有考虑到这个问题。这样就不应该只给 7 分了。</p></li><li class=\"reply\" data-id=942><p class=\"reply-info\">作者<a href=\"/user/profile?userId=161\">郭菁华</a>&nbsp;回复&nbsp;<span class=\"time\">04-17 21:13', '0', '9130', '2016-04-16 16:49:00'), ('261', null, '丘翔', null, '我看错了，原来你们是用了 object-fix: cover 的，之前是在 IE 下看到的图片被压缩，以为你们没有考虑到这个问题。这样就不应该只给 7 分了。', '260', null, '2016-04-18 00:52:00'), ('262', null, '郭菁华', null, '一开始就打算专注css，就有种一条道走到黑的感觉，不过还是被迫写了js的fix（笑）。没有使用不同的class来区别不同布局是因为我不喜欢在库中加入一些约定，使用class就意味着文档中就要提示用户在自己的代码注意不要破坏这些class，所以在可接受的牺牲范围内，选择了目前的方案。居中的问题当初确实考虑过使用background-size:cover，但是感觉使用background-image实现不如&lt;img/&gt;优雅（background-image元素在右键菜单中并不会被当作图片处理），而且题目的clip-path就不能ie、edge处理，所以忽略了ie，拥抱object-fit。js封装性的问题确实是个失误，错误地对外暴露了init()。', '260', null, '2016-04-17 21:13:00'), ('263', '小攻城狮', '连立麟', '9', '可以请教一下你js代码的思路吗？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1047><p class=\"reply-info\"><a href=\"/user/profile?userId=5737\">连立麟</a>&nbsp;回复&nbsp;<span class=\"time\">04-29 11:46</span></p><p class=\"reply-content\">回答的非常好！谢谢，受教了</p></li><li class=\"reply\" data-id=1043><p class=\"reply-info\">作者<a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">04-28 20:24', '0', '9174', '2016-04-27 15:12:00'), ('264', null, '连立麟', null, '回答的非常好！谢谢，受教了', '263', null, '2016-04-29 11:46:00'), ('265', null, '余思烨', null, 'js插件的写法，分为三部分，一开始的分号为了多个JS文件合并压缩的时候防止文件之间没有;分隔导致错误，第一部分是主要是为了扩展性，支持模块化。可以参考：http://stackoverflow.com/questions/14362047/vanilla-js-plugin-template，不少插件都有这样的写法。第二部分就是一个构造器，第三部分是原型链添加方法，使用的都是最基本的封装技巧。为了不污染全局环境变量，整个封装都是在闭包里实现的。这里我考虑到正方形的这个方法并不是每一个每个类都需要，只有在三个图片和五个图片的时候才需要，所以就把它从原型链移除。这里用的注释是JSdoc，第四阶段的任务有提到，好处是可以自动生成类似readme的html文档。不知道这样有没有解答到你的疑惑？', '263', null, '2016-04-28 20:24:00'), ('266', 'Lafairy', '龚沛', '8', 'wow新版本js好精简！ 还支持amd cmd好评。提一点小小的意见，containerSelector是不是太长了一点，感觉打起来比较费劲【x   然后如果能美化下title的css就更好了', '0', '9174', '2016-04-19 23:41:00'), ('267', '锟斤拷', '丘翔', '8', '我不是个喜欢鸡蛋里挑骨头的人，你们做得很好了，实现的效果没得说，封装得也很好。<br />\r\n<br />\r\n指出一个 bug，第5个布局，因为用了浮动，长宽比大于2的时候（比如设置 mixphotoContainer-5 { height: 400px; }），会出现布局错误。为了解决这个问题，可以考虑用绝对定位。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=962><p class=\"reply-info\">作者<a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 14:56', '0', '9174', '2016-04-19 14:05:00'), ('268', null, '余思烨', null, '谢谢提醒bug，采纳绝对定位的建议，修改了CSS，已经改好了。<br />\r\n<br />\r\n其实一开始的版本惨不忍睹，没有封装，没有考虑图片剪裁，直接用JS控制样式等等第四阶段提到的所有问题我都占了，十分十分羞愧。看到之后刷Stack Overflow，看剪裁图片，看代码封装，模块化，看Github别人写的插件代码，然后才重写了这个版本。现在看回来能得到这样的评价肯定真的觉得值得。容我再次谢谢你 T T', '267', null, '2016-04-19 14:56:00'), ('269', 'steel team', '欧阳湘粤', '7', '基本完成需求，但是很多细节需要加强<br />\r\n<br />\r\n例如清一清 outline</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=954><p class=\"reply-info\">作者<a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 00:16', '0', '9174', '2016-04-18 17:14:00'), ('270', null, '余思烨', null, '谢谢你的review，其实在看了其他人写的代码后我早就不满意这个版本了，除了封装失败以外，许多细节也忽略，这次重写了一遍，优化了js，实现布局重点在于css，js用来修正正方形，希望你有空能再看一下', '269', null, '2016-04-19 00:16:00'), ('271', null, null, '8', '总体不错，能够用css而非js实现效果。gallery.setAttribute(&#039;class&#039;,&#039;g-wrap&#039;); 这一段switch不是很有意义，你完全可以把类名叫做g-wrap-1到g-wrap-6，这样gallery.setAttribute(&#039;class&#039;,&#039;g-wrap&#039; + item); 就行了</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1068><p class=\"reply-info\">作者<a href=\"/user/profile?userId=915\">史文俊</a>&nbsp;回复&nbsp;<span class=\"time\">05-04 11:00</span></p><p class=\"reply-content\">感谢您的指导，感谢你们的review</p></li><li class=\"reply\" data-id=1057><p class=\"reply-info\"><a href=\"/user/profile?userId=3367\">刘彬德</a>&nbsp;回复&nbsp;<span class=\"time\">05-01 17:37', '0', '10166', '2016-04-27 14:44:00'), ('272', null, '史文俊', null, '感谢您的指导，感谢你们的review', '271', null, '2016-05-04 11:00:00'), ('273', null, '刘彬德', null, '可是，纯css实现的样式导致了图片的拉伸啊，要求中不是图片不可拉伸么，多余部分要进行裁剪', '271', null, '2016-05-01 17:37:00'), ('274', 'ZLFE', '刘彬德', '7', '纯css的实现中没有考虑到图片的拉伸和裁剪啊', '0', '10166', '2016-05-01 17:38:00'), ('275', 'sunshine', '徐亚光', '7', '我就一个想说。我没看出来这是封装成一个图片库啊。。。', '0', '9156', '2016-04-24 19:31:00'), ('276', 'ZLFE', '刘彬德', '7', '看了代码，很简洁。但从ife第四期的任务要求来看，官方更推荐改变class来改变样式而不是直接在style中添加样式。<br />\r\n<br />\r\nie的兼容性没有看，FF样式二会挂。<br />\r\n<br />\r\nps，欢迎review  http://ife.baidu.com/review/detail?workId=10915', '0', '9211', '2016-05-01 14:45:00'), ('277', 'code matters', '彭君怡', '7', '谷歌浏览器一切正常，但是火狐里面的2号布局挂了，是我一个人的现象吗？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=911><p class=\"reply-info\"><a href=\"/user/profile?userId=161\">郭菁华</a>&nbsp;回复&nbsp;<span class=\"time\">04-15 19:19</span></p><p class=\"reply-content\">chrome的-webkit-clip-path支持shape和url(仅限内联svg)，firefox的clip-path只支持url形式。不过如果是新版的firefox（大于46）可以开启layout.css.clip-path-shapes.enabled。</p></li><li class=\"reply\" data-id=902><p class=\"reply-info\">作者<a href=\"/user/profile?userId=48\">赵宇翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-15 09:48</span></p><p class=\"reply-content\">我在描述里有说明的。两张图片的布局需要i在chrome下查看，因为用的是IFE任务参考资料里的clip-path</p></li><li class=\"reply\" data-id=894><p class=\"reply-info\"><a href=\"/user/profile?userId=99\">李昕宇</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 20:16', '0', '9211', '2016-04-14 20:12:00'), ('278', null, '郭菁华', null, 'chrome的-webkit-clip-path支持shape和url(仅限内联svg)，firefox的clip-path只支持url形式。不过如果是新版的firefox（大于46）可以开启layout.css.clip-path-shapes.enabled。', '277', null, '2016-04-15 19:19:00');
INSERT INTO `ife_review` VALUES ('279', null, '赵宇翔', null, '我在描述里有说明的。两张图片的布局需要i在chrome下查看，因为用的是IFE任务参考资料里的clip-path', '277', null, '2016-04-15 09:48:00'), ('280', null, '李昕宇', null, '不是，，，FF要自己用SVG去画', '277', null, '2016-04-14 20:16:00'), ('281', '嗨！贾克斯', '余思烨', '7', '封装的挺好，喜欢你的动画效果。<br />\r\n<br />\r\n提点意见，控制台打印的代码不去掉吗？wrap的传递可以用一个css选择器，然后再传递进document.querySelector()，这样就不用每次初始化的时候都要先打getElemntById或者其他方法获取加字符串了<br />\r\n（话说图片都是我的桌面壁纸当然好看，图片拿走了，review都不留下吗？）<br />\r\n<br />\r\n再次更新，你直接链接我的github上的资源，现在我图片移除了，然后你图片就挂了，表示歉意，你不考虑更新一下你的资源吗？如果你要我之前的壁纸的话，可以私发你邮箱。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=965><p class=\"reply-info\">作者<a href=\"/user/profile?userId=1404\">龚沛</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 21:15', '0', '9396', '2016-04-17 10:29:00'), ('282', null, '龚沛', null, '噗不好意思不好意思之前没看到review。谢谢建议！传选择器这样确实比较方便，欠考虑了0 0      没事没事我去换掉一下图片，直接用了你的链接不好意思。', '281', null, '2016-04-19 21:15:00'), ('283', null, null, '7', '代码整体风格比较规范，注意多用css实现样式上的效果，尽可能少用js。参见的：http://ife.baidu.com/task/detail?taskId=51 的“第三阶段回顾”', '0', '9002', '2016-04-27 14:46:00'), ('284', 'steel team', '欧阳湘粤', '6', '其实基本效果已经实现了，但是希望以后发布的时候注明浏览器的兼容性，不然实际上也是影响你们团队的得分<br />\r\n<br />\r\n样式上还需要再多些美化，起码整个页面要居中', '0', '9002', '2016-04-18 17:18:00'), ('285', '五个非洲人', '李昕宇', '6', 'IE,FF浏览器没有做出来梯形的效果，，，，，，虽然chrome为第一浏览器，但是也不能忘了后面几个吧。我觉得JS还可以优化一下，所以就给6分吧', '0', '9002', '2016-04-14 14:51:00'), ('286', 'Sunday', '余小威', '5', '为什么搜狗浏览器，图片排的不对</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=886><p class=\"reply-info\">作者<a href=\"/user/profile?userId=7879\">金黎明</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 15:03', '0', '9002', '2016-04-14 12:31:00'), ('287', null, '金黎明', null, '感谢反馈，刚刚测试发现很多国产的浏览器在使用for-in语句时，遍历的结果与Chrome浏览器不一样，导致js执行过程中产生了异常终止。<br />\r\n我已经将for-in语句改为了一般的for循环遍历。<br />\r\n在测试过程中，还发现了国产浏览器在获取图片大小的过程中，获取到的结果也和Chrome不一样，导致了第二次排版问题，我也已经解决了。', '286', null, '2016-04-14 15:03:00'), ('288', null, null, '6', '裁掉图片时，没有将图片放在中间裁去，参见 http://ife.baidu.com/task/detail?taskId=51 的“第三阶段回顾”。如果作为第三方库，需要尽可能减少对其他库的依赖，这里用了 Vue，但其实不是很有必要，希望在第四阶段的时候能够减少依赖。', '0', '9158', '2016-04-27 14:29:00'), ('289', '锟斤拷', '丘翔', '6', '你的图片接口用了我的服务器。。我是不介意啦，不过可能会因为我对接口的调整而影响到你的页面，所以我建议你用另一个地址 http://gallery-server.coding.io/，或者你自己搭建一个服务器，服务端的代码在 https://github.com/qiuxiang/gallery-server。<br />\r\n<br />\r\n有个 bug 似乎没人注意到，<br />\r\n&#039;&lt;div class=&quot;pic-box gallery-1&quot;&gt;&lt;div class=&quot;gallery&quot; style=background-Image:url(&quot;&#039;+img[0].src+&#039;&quot;&gt;&lt;/div&gt;&lt;/div&gt;&#039;<br />\r\n这里双引号的使用是有问题的，style 不带引号也就算了，url 却带引号，这会让浏览器的解析出现歧义，导致我这边的 chrome 是显示不出第一张图片的。<br />\r\n<br />\r\n很多问题1楼已提到，这里讨论一些代码问题。<br />\r\n<br />\r\nM = Math,<br />\r\nR = Math.random,<br />\r\n这样的别名有什么意义呢？只是为了更短？但这样牺牲了可读性呀。<br />\r\n<br />\r\nfunction E() {<br />\r\n   throw new Error(&quot;Missing parameter&quot;);<br />\r\n}<br />\r\n封装的意义？我看这个函数作为 createPic(url=E()) 默认参数，最终用于 img.src = url，这做法也有问题呀。<br />\r\n<br />\r\naddClass，removeClass 的封装也是没必要的，因为已经有 classlist 了，具体参考 https://developer.mozilla.org/zh-CN/docs/Web/API/Element/classList。<br />\r\n<br />\r\n+function () {<br />\r\n...<br />\r\n}();<br />\r\n几处匿名函数的使用也没有必要，内部并没有声明新的局部变量。<br />\r\n<br />\r\n我觉得很重要的一点没有做到：拼图布局封装成独立可复用的模块。', '0', '9514', '2016-04-19 11:57:00'), ('290', '嗨！贾克斯', '余思烨', '6', '加载动画loading很酷炫，用es6的Promise加载要点赞，用css做主要布局的思路是正确的，提点小建议：<br />\r\n<br />\r\n关于布局：其中两个布局图片的展示格式不太对，两张图片的时候，没有从三分之一宽度开始切割，三张图片和五张图片的正方形效果没又做出来，感觉在不限定每个图片具体像素的情况下纯CSS做不了正方形（希望能打脸）。<br />\r\n<br />\r\n关于思路：做任务，首先是完成需求，然后再拓展，需求的布局没有做好的情况下，还是不要先做其他额外的功能，因为这样的话思路很容易混乱，当然因人而异，这是我自己的想法，欢迎一起讨论。<br />\r\n<br />\r\n分数：最基本布局需求没有达到，所以给的分数并不高，如果改好了，我会回来再给分数的。<br />\r\n<br />\r\n另：经过你的review我又重新修改了一下代码，欢迎再去review。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1074><p class=\"reply-info\"><a href=\"/user/profile?userId=3367\">刘彬德</a>&nbsp;回复&nbsp;<span class=\"time\">05-08 01:49', '0', '9514', '2016-04-19 10:48:00'), ('291', null, '刘彬德', null, '哈哈，伪打脸来啦', '290', null, '2016-05-08 01:49:00'), ('292', '2+1', '丘玉秀', '10', '效果真的很棒！', '0', '9512', '2016-05-02 14:42:00'), ('293', 'win  or out', '张如意', '10', '做的确实不错', '0', '9512', '2016-05-01 08:31:00'), ('294', 'code matters', '彭君怡', '9', '给人很清爽的感觉，代码也非常的简洁漂亮，但是还是觉得在难度上，那个自己搭接口传照片进来的要厉害点，所以就扣一分好了，自己的代码无论从美观还是简洁度上都差着很远呢，学习了！！</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1045><p class=\"reply-info\">作者<a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">04-28 20:40', '0', '9512', '2016-04-28 20:31:00'), ('295', null, '余思烨', null, '谢谢，从这次任务中学到很多，一起互相学习！', '294', null, '2016-04-28 20:40:00'), ('296', '小攻城狮', '连立麟', '10', '这个效果真是棒棒的……展示也一目了然，学习了<br />\r\n<br />\r\n可以请教一下waterfall.js的思路吗？尤其开头部分<br />\r\n flex-direction: column;遮盖层的这个属性去掉好像不影响效果，目的是？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1048><p class=\"reply-info\"><a href=\"/user/profile?userId=5737\">连立麟</a>&nbsp;回复&nbsp;<span class=\"time\">04-29 16:41</span></p><p class=\"reply-content\">真是太感谢了，回答的很详细，你代码很多细节初看之下没理解，我自己写的时候再返回去看能明白很多你的用意，学到很多~祝好运</p></li><li class=\"reply\" data-id=1044><p class=\"reply-info\">作者<a href=\"/user/profile?userId=134\">余思烨</a>&nbsp;回复&nbsp;<span class=\"time\">04-28 20:35', '0', '9512', '2016-04-28 11:23:00'), ('297', null, '连立麟', null, '真是太感谢了，回答的很详细，你代码很多细节初看之下没理解，我自己写的时候再返回去看能明白很多你的用意，学到很多~祝好运', '296', null, '2016-04-29 16:41:00'), ('298', null, '余思烨', null, 'waterfall的思路其实在于，收集相应的div放进一个容器里面，然后根据获取目前column中最小的高度逐个添加到column上面，开头的if语句主要是给JS模块化做扩展，具体你可以参考 http://stackoverflow.com/questions/14362047/vanilla-js-plugin-template 。注释部分用JSdoc，好处是可以自动生成readme的HTML文档，第四阶段的任务有提到。<br />\r\n这个flex-direction去掉确实可以的，因为在只有一个元素的情况下，无论修改成什么值都没有影响。已经改掉了，谢谢你的提醒！', '296', null, '2016-04-28 20:35:00'), ('299', 'code matters', '彭君怡', '10', '看你们队的代码我两个膝盖都不够跪的。。还是比较打击我的自信心的。。真的有种看完你们代码发现差距太大了，还看不懂。。总之很牛！！学习！', '0', '9059', '2016-04-28 20:33:00'), ('300', 'ZRLLL', '宗维康', '10', '非常棒  node的代码也学习了  给10分 不怕骄傲了</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1034><p class=\"reply-info\"><a href=\"/user/profile?userId=2590\">宗维康</a>&nbsp;回复&nbsp;<span class=\"time\">04-27 13:39</span></p><p class=\"reply-content\">好的 我试一下coding</p></li><li class=\"reply\" data-id=1027><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-26 16:28</span></p><p class=\"reply-content\">没问题，只是，建议自己搭，如果没有自己的服务器可以考虑用 coding 的在线演示功能。因为近期我可能会改服务端 API，为此受影响导致你的页面出错就不好了。</p></li><li class=\"reply\" data-id=1025><p class=\"reply-info\"><a href=\"/user/profile?userId=2590\">宗维康</a>&nbsp;回复&nbsp;<span class=\"time\">04-26 11:32</span></p><p class=\"reply-content\">能不能借用一下你的图片服务器？</p></li><li class=\"reply\" data-id=1016><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-25 17:03', '0', '9059', '2016-04-25 15:16:00'), ('301', null, '宗维康', null, '好的 我试一下coding', '300', null, '2016-04-27 13:39:00'), ('302', null, '丘翔', null, '没问题，只是，建议自己搭，如果没有自己的服务器可以考虑用 coding 的在线演示功能。因为近期我可能会改服务端 API，为此受影响导致你的页面出错就不好了。', '300', null, '2016-04-26 16:28:00'), ('303', null, '宗维康', null, '能不能借用一下你的图片服务器？', '300', null, '2016-04-26 11:32:00'), ('304', null, '丘翔', null, '谢谢～', '300', null, '2016-04-25 17:03:00'), ('305', 'LZZ神秘代码', '周延博', '10', '500px 的图怎么下载的？好厉害啊</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=995><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-24 00:26', '0', '9059', '2016-04-23 20:22:00'), ('306', null, '丘翔', null, '你看服务端的源代码就知道了 https://github.com/qiuxiang/gallery-server.git', '305', null, '2016-04-24 00:26:00'), ('307', '青训营', '胡思琴', '8', '请问图片是自己收集的还是自己制作的？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=952><p class=\"reply-info\"><a href=\"/user/profile?userId=9111\">胡思琴</a>&nbsp;回复&nbsp;<span class=\"time\">04-18 21:06</span></p><p class=\"reply-content\">谢谢</p></li><li class=\"reply\" data-id=944><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-18 01:03', '0', '9059', '2016-04-17 22:09:00'), ('308', null, '胡思琴', null, '谢谢', '307', null, '2016-04-18 21:06:00'), ('309', null, '丘翔', null, '图片来自 500px，你用开发者工具看下网络请求，就可以看出一些端倪了。', '307', null, '2016-04-18 01:03:00'), ('310', '夜魇天辉', '李应劼', '9', '做得好爽，mark一下，学习！</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=893><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 20:09', '0', '8914', '2016-04-14 18:52:00'), ('311', null, '陈慕杰', null, '谢谢review~ n(*≧▽≦*)n 可以下载下来试试三列或者五列或更多~', '310', null, '2016-04-14 20:09:00'), ('312', 'PreWebDeveloper', '陈志斌', '9', '应该是误会了.....<br />\r\n第一行写的这么暧昧谁不想歪--</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=889><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 16:14</span></p><p class=\"reply-content\">没关系, 原谅你了~</p></li><li class=\"reply\" data-id=882><p class=\"reply-info\"><a href=\"/user/profile?userId=7989\">刘嘉航</a>&nbsp;回复&nbsp;<span class=\"time\">04-14 10:54</span></p><p class=\"reply-content\">心疼作者</p></li><li class=\"reply\" data-id=870><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 18:15</span></p><p class=\"reply-content\">被1分支配的心理阴影...</p></li><li class=\"reply\" data-id=869><p class=\"reply-info\"><a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 16:45</span></p><p class=\"reply-content\">确实是误会了</p></li><li class=\"reply\" data-id=867><p class=\"reply-info\"><a href=\"/user/profile?userId=1095\">杨健</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 16:40</span></p><p class=\"reply-content\">笑死</p></li><li class=\"reply\" data-id=856><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 08:36', '0', '8914', '2016-04-13 08:32:00'), ('313', null, '陈慕杰', null, '没关系, 原谅你了~', '312', null, '2016-04-14 16:14:00'), ('314', null, '刘嘉航', null, '心疼作者', '312', null, '2016-04-14 10:54:00'), ('315', null, '陈慕杰', null, '被1分支配的心理阴影...', '312', null, '2016-04-13 18:15:00'), ('316', null, '丘翔', null, '确实是误会了', '312', null, '2016-04-13 16:45:00'), ('317', null, '杨健', null, '笑死', '312', null, '2016-04-13 16:40:00'), ('318', null, '陈慕杰', null, '卧槽!  哪里使用县城瀑布布局的库了,,我就用了JQuery而已呀, 那个是我自己写的阿喂!!!! (°ー°〃)', '312', null, '2016-04-13 08:36:00'), ('319', 'yyqqhh', '刘建环', '8', '已经实现的可算是很完美了啊，很多细节问题都注意到了。我说一下我在体验过程中的问题：就是可能会由于网速问题导致访问效果很差。因为图片加载相当慢。应该可以继续优化的。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=868><p class=\"reply-info\"><a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 16:43</span></p><p class=\"reply-content\">我是觉得，这里就没必要死抠图片加载了，因为任务的主题就无关加载优化。很显然，这里的图片加载慢很很大一定程度是因为服务器不在国内而已。</p></li><li class=\"reply\" data-id=866><p class=\"reply-info\">作者<a href=\"/user/profile?userId=2329\">陈慕杰</a>&nbsp;回复&nbsp;<span class=\"time\">04-13 16:33', '0', '8914', '2016-04-13 16:27:00'), ('320', null, '丘翔', null, '我是觉得，这里就没必要死抠图片加载了，因为任务的主题就无关加载优化。很显然，这里的图片加载慢很很大一定程度是因为服务器不在国内而已。', '319', null, '2016-04-13 16:43:00'), ('321', null, '陈慕杰', null, '谢谢review~ 为了表现能查看完整大图片， 所以都选了些蛮大的图片， 所以导致图片加载有点慢~~', '319', null, '2016-04-13 16:33:00'), ('322', null, null, '5', '### HTML<br />\r\n<br />\r\n1. 指定字符编码的 meta 应该作为 head 的第一个子元素<br />\r\n2. &lt;html&gt; 应该指定 lang 属性<br />\r\n3. link、script 标签的 type 属性冗余<br />\r\n4. 避免直接在 style 属性中书写样式<br />\r\n5. HTML 中的 JS 代码也需要遵循公认的代码规范<br />\r\n<br />\r\n### CSS<br />\r\n<br />\r\n1. 避免使用 `*` 定义样式<br />\r\n2. 建议 `{` 前增加一个空格<br />\r\n3. 避免 selector 中嵌套层次超过 3 级<br />\r\n<br />\r\n### JavaScript<br />\r\n<br />\r\n1. 建议遵循社区约定俗成的代码规范，适当增加空格或空行来提高代码可读性<br />\r\n2. parameter 的缩写惯用法是 param，复数 params<br />\r\n3. 重写 MakeFalls 的 prototype 需要修复 constructor<br />\r\n4. 属性及配置项 gap 的设计，应该直接使用数字，方便计算<br />\r\n5. 批量创建的 DOM 建议先放到 fragment，最后再一起插入 DOM tree<br />\r\n6. 多次操作的对象、数据，应存到本地变量再操作<br />\r\n7. 多次出现的 magic number 20，应该声明为常量，或者可由外部配置<br />\r\n8. addDrip 可以优化成 addLine，在算法上会更合理些<br />\r\n9. 求最短的列，可以通过每次添加图片时计算，缓存下来<br />\r\n10. 总体封装还不够，外部可配置性不高</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1061><p class=\"reply-info\">作者<a href=\"/user/profile?userId=5737\">连立麟</a>&nbsp;回复&nbsp;<span class=\"time\">05-03 12:07', '0', '10899', '2016-05-03 11:50:00'), ('323', null, '连立麟', null, '谢谢老师，祝工作顺利', '322', null, '2016-05-03 12:07:00'), ('324', '学习小组', '张伟', '10', '学习了，好厉害', '0', '9103', '2016-05-05 09:31:00'), ('325', 'code matters', '彭君怡', '10', '换算图片宽高比的懂了，但是请问一下我动态抓取图片并且创建IMG的时候，图片的宽高往往都加载的慢得到的值都是0，百度了下一般都用img.onload获取，你们是通过什么方法获取每张图的宽高的呢？</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1058><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">05-02 11:49', '0', '9103', '2016-05-01 10:17:00'), ('326', null, '丘翔', null, '长宽是由服务端接口提供的，load 事件虽然可以，但缺点在于必须等待图片加载。', '325', null, '2016-05-02 11:49:00'), ('327', 'ZRLLL', '宗维康', '9', '比如clientWidth为1500，那么按照300为高度，去换算图片的宽度，相加之和大于1500时（比如是1700），那么之前的图片作为一行，之后再300×（1500/1700）做为行高，对吧</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1028><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-26 21:06', '0', '9103', '2016-04-26 17:21:00'), ('328', null, '丘翔', null, '是的', '327', null, '2016-04-26 21:06:00'), ('329', 'steel team', '欧阳湘粤', '10', '实际上这次比赛的最核心的地方就是相互review<br />\r\n<br />\r\n虽然不能完全看懂你的代码，但是还是尽量想去多学些东西<br />\r\n<br />\r\n有些东西想问一下你：<br />\r\n<br />\r\n/**<br />\r\n * 木桶布局<br />\r\n * <br />\r\n * @param {string} selector<br />\r\n * @param {int} [minHeight=300]<br />\r\n * @constructor<br />\r\n */<br />\r\n<br />\r\n实现这种功能的是什么插件吗？<br />\r\n<br />\r\n<br />\r\n题外话：兄弟，其实虽然熊厂不社招了，你也可以选择鹅厂和阿里啊，75也很牛逼的</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=955><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-19 00:59', '0', '9103', '2016-04-18 20:07:00'), ('330', null, '丘翔', null, '我同意你的看法，我是很希望可以和大家一起讨论技术实现的优劣权衡，自己所想未必是最好的。<br />\r\n<br />\r\n/**<br />\r\n * @constructor<br />\r\n * @param {string} selector<br />\r\n */<br />\r\n<br />\r\n这种不是什么功能，而是文档注释，规定注释按某种格式书写，方便自动化文档生产，具体参考 JSDoc（http://usejsdoc.org/）。我个人的主张是尽量少写文档注释以外的说明性注释，如果程序结构设计得很优秀，命名做到直观、统一，那么代码本身读起来就很流畅，这时候过多的注释反而显得啰嗦重复。<br />\r\n<br />\r\n好好看下 JSDoc 的说明，手打文档注释并不是问题，当然，好点的 IDE（如果是 sublime 之类的编辑器，应该也会有相应的插件，没有过多了解）会让你书写文档注释的时候更舒畅。<br />\r\n<br />\r\n然后聊到我为什么要参加这次的春季班，其实我是被朋友忽悠过来的……她说想要提升自己的前端水平，缺队友，一方面我想也想帮到她，另一方面，自己也可以练练手，就来了。结果直到现在，我做了不少题目，她自己却没做多少 (´Д` )', '329', null, '2016-04-19 00:59:00'), ('331', '前端热爱者', '吴立宁', '9', '太强,只能膜拜一下<br />\r\n我题目一直没看懂,意思是高度相同的图片放到一行,并且只能放3-6个吗?,要是有几个特别的高的或者低的图片怎么办?<br />\r\n去看了你的源码,好高级,明显不是初学者,源码不容易看懂,比如你的photos是哪里来的,准备一个一个commit看,第一次commit就有个promise...</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=948><p class=\"reply-info\"><a href=\"/user/profile?userId=517\">吴立宁</a>&nbsp;回复&nbsp;<span class=\"time\">04-18 15:30</span></p><p class=\"reply-content\">谢谢你的回答</p></li><li class=\"reply\" data-id=939><p class=\"reply-info\">作者<a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-17 20:55', '0', '9103', '2016-04-17 18:54:00'), ('332', null, '吴立宁', null, '谢谢你的回答', '331', null, '2016-04-18 15:30:00'), ('333', null, '丘翔', null, '我觉得木桶布局的要点不在于每行放多少张图片，而是尽量让每行高度差不多，比如我这里设定的 300px。如果理解为高度相同的图片放到一行就不对了，图片的顺序不应该被打乱。有一些图片宽高比特别高或特别低，无非就是在一行里多放或少放的问题。<br />\r\n<br />\r\n这里的代码主要用了 js 面向对象的写法，可以参考这里 https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Introduction_to_Object-Oriented_JavaScript。<br />\r\n<br />\r\n对于这个页面，我主要划分了3个模块，核心布局 GalleryRows 负责实现木桶布局，弹出层 Modal 负责实现大图的显示，应用层 Application 对各模块进行组织以及页面逻辑的实现。其中图片的获取在 Application 里进行调用，源代码是 utils.js 里的 getPhotos()，通过 ajax 方式获取某个图片分享平台的图片列表。<br />\r\n<br />\r\n不建议你看 commit……一方面这个项目是3个相册一起实现的，最初的 commit 还没开始做这个任务。另一方面，中间的过程我一直在思考和改进，代码结构也一直在变。<br />\r\n<br />\r\npromise 是更好地处理 js 回调的一种技术，在 es6 已经是标准模块，这个项目里使用的 promise 是我自己实现的一个简单版本。你可以自己查看相关资料了解 https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Reference/Global_Objects/Promise', '331', null, '2016-04-17 20:55:00'), ('334', null, null, '7', '总体实现挺赞的。<br />\r\n<br />\r\n页面初始化排版混乱这点可以改进一下。<br />\r\n<br />\r\n提几点建议：<br />\r\n<br />\r\n1. 缓存for循环的数组长度是一个好习惯： var i = 0; i &lt; this.boxes.length; i++  =&gt; var i = 0, len = this.boxes.length; i &lt; len; i++ <br />\r\n<br />\r\n2. Array.prototype.slice.call(this.container.querySelectorAll(boxSelector)) 这一步转换成数组有点多余，querySelectorAll返回本身是一个类数组的结构，如果不是想使用array的map、forEach等数组方法，不建议转换<br />\r\n<br />\r\n3、程序层次结构不大清晰，可稍做调整下。清晰的程序结构可以直观地看出完成这个任务需要的步骤， 每个步骤通过一个方法实现，比如这个代码可以总结为以下几个步骤：<br />\r\ncalcRatio<br />\r\ncalcRows<br />\r\ninitRows<br />\r\nrender', '0', '9798', '2016-05-05 15:06:00'), ('335', null, null, '0', 'demo有错，只是个loading页面，我还以为你们做的是loading特效呢。。。</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1056><p class=\"reply-info\"><a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">05-01 15:38</span></p><p class=\"reply-content\">用babel编译成es5的格式吧。。。另外后来导师也一直没有修改给你的评分么？心疼你。。。</p></li><li class=\"reply\" data-id=1026><p class=\"reply-info\">作者<a href=\"/user/profile?userId=1110\">欧阳湘粤</a>&nbsp;回复&nbsp;<span class=\"time\">04-26 13:34</span></p><p class=\"reply-content\">可能是使用了较新的es6导致，因为最近在学ES6就用了...</p></li><li class=\"reply\" data-id=984><p class=\"reply-info\"><a href=\"/user/profile?userId=339\">周天树</a>&nbsp;回复&nbsp;<span class=\"time\">04-22 15:48', '0', '9661', '2016-04-22 11:27:00'), ('336', null, '周天树', null, '用babel编译成es5的格式吧。。。另外后来导师也一直没有修改给你的评分么？心疼你。。。', '335', null, '2016-05-01 15:38:00'), ('337', null, '欧阳湘粤', null, '可能是使用了较新的es6导致，因为最近在学ES6就用了...', '335', null, '2016-04-26 13:34:00'), ('338', null, '周天树', null, '没问题啊。。。Loading到100%就出页面了。。。<br />\r\n测试浏览器：Chrome 50.0.2661.87', '335', null, '2016-04-22 15:48:00'), ('339', '前端热爱者', '吴立宁', '10', '向你们学习<br />\r\n还有,你们的图片都是来自&quot;drscdn.500px.org&quot;<br />\r\n这是什么网站,怎么没找到关于这个网站的介绍</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=968><p class=\"reply-info\">作者<a href=\"/user/profile?userId=1110\">欧阳湘粤</a>&nbsp;回复&nbsp;<span class=\"time\">04-20 15:58</span></p><p class=\"reply-content\">评论旁有个编辑按钮...</p></li><li class=\"reply\" data-id=967><p class=\"reply-info\"><a href=\"/user/profile?userId=517\">吴立宁</a>&nbsp;回复&nbsp;<span class=\"time\">04-20 14:10</span></p><p class=\"reply-content\">我擦怎么打了默认分数</p></li><li class=\"reply\" data-id=966><p class=\"reply-info\"><a href=\"/user/profile?userId=3310\">丘翔</a>&nbsp;回复&nbsp;<span class=\"time\">04-20 13:22', '0', '9661', '2016-04-19 23:14:00'), ('340', null, '欧阳湘粤', null, '评论旁有个编辑按钮...', '339', null, '2016-04-20 15:58:00'), ('341', null, '吴立宁', null, '我擦怎么打了默认分数', '339', null, '2016-04-20 14:10:00'), ('342', null, '丘翔', null, '我来解释下，图片是来自 https://500px.com/，这里调用的是我写的一个在线接口，http://gallery-server.coding.io/?source=500px&amp;page=1', '339', null, '2016-04-20 13:22:00'), ('343', '小攻城狮', '连立麟', '2', '亲……谷歌打开格式是乱的啊</span><ul class=\"reply-wrap\"><li class=\"reply\"style=\"display:block;\" data-id=1073><p class=\"reply-info\">作者<a href=\"/user/profile?userId=1985\">钱晟</a>&nbsp;回复&nbsp;<span class=\"time\">05-07 14:52', '0', '10981', '2016-05-04 14:14:00'), ('344', null, '钱晟', null, '发现是因为之前master的代码没有并到demo的分支上去= =已更新', '343', null, '2016-05-07 14:52:00'), ('345', null, null, '2', '1. 排版库抽象度不够，在其他地方不能使用排版库<br />\r\n2. 算法明显需要优化，排序之后进行排版效果会更好', '0', '11003', '2016-05-18 09:50:00'), ('346', 'Go', '孔阿栋', '9', '好奇怪，同样用requestAnimation，我按照你的speed也修改为每帧走1px，但是移动起来很不流畅，为什么你的坦克移动起来就很流畅啊', '0', '10911', '2016-05-28 15:07:00'), ('347', '泛太平洋前端团队', '富显祖', '